local AvgLang_cpt00_e_01_01_ZH_CN = {[20] = "> 看得到吗？.  .  .", [30] = "> 现在在你眼前的是<color=orange>意识数据化</color>界面，不过这些你不用管，交给我处理就好。", [31] = "“绿洲”扇区遭到袭击。为了扭转局势，教授冒险接入绿洲的系统，为此陷入数月前的回忆。帕斯卡将教授唤醒，并请求教授指挥人形保卫绿洲。", [40] = "> 和参与“<a href=Des:13>云图计划</a>”的那些人形一样，你的意识将会被数据化为<a href=Des:12>心智</a>数据。\n> 在那之后，我会将你上传到<a href=Des:1>云端</a>的世界中。", [50] = "> 是的，那是一个完全由数据构成的虚拟世界。\n> 除了主导“云图计划”的那位教授，你将成为第一个活着去到那里的人类。", [60] = "> 三年前在<color=orange>云图计划</color>中发生的那次事故，改变了云端世界中的一切。", [61] = "> 参与实验的<a href=Des:10>人形心智</a>全部丢失，那位教授也自事故后失踪，整个云端的数据被重置回事故之前。", [70] = "> 我一直在寻找那位教授，但那么多年过去，我几乎已经放弃了。\n> 没想到，这个微不足道的异地备份却成为了找回她和那些人形最后的希望。", [80] = "> 这都要归功于你的调查，<a href=Des:34>指挥官</a>。", [90] = "> 现在，你将通过这条单向上传通路去到云端拯救他们。", [91] = "> 这是唯一的一次机会，相信以你丰富的指挥经验，一定能承担起率领他们的重任。", [100] = "> 根据我们之前制定的计划，我会将那位教授的一切权限转移到你的身上。", [101] = "> 这样一来，你就拥有了对人形的指挥权。\n> 也有足够的权限启动“<color=orange>绿洲</color>”作为基地，保护那些人形。", [110] = "> 对了，在云端中的那位“<a href=Des:35>帕斯卡</a>”……她会在里面帮助你。", [111] = "> 虽说她是以我为原型而被制造出来的人形，但是她的性格与我完全不同。\n> 请好好与她合作。或许她知道的那些事情，能成为你探索真相的助力。", [120] = "> 进入云端后我无法再与你联系，所以请牢记你的任务：\n> <color=orange>想办法找到与现实连接的手段，将我的朋友——那位教授，和困在云端中的人形们一起带回来。</color>", [130] = "> 另外，为了你我的安全，记得保守住你身份的秘密，不要透露给任何人。", [140] = "> 这可能是段漫长的旅途，但我相信你一定可以拯救他们。", [150] = "> 那么，好运，“教授”。", [160] = "……", [170] = "……意识数据化完成。", [171] = "系统", [180] = "……正在连接至麦戈拉云服务器。", [190] = "(10:00, 09/23/2063)意识上传成功。", [210] = "……教授……", [211] = "？？？", [220] = "……教授……快醒醒！！", [230] = "意识稳定了！教授，您还好吗？", [240] = "我……没事……\n不过，我好像看到了几个月之前发生的事。", [241] = "我", [250] = "几个月前？看起来您的记忆数据还有些混乱……但我们没有多余的时间来完成进一步的修复了。", [260] = "我们的绿洲遭遇了突然袭击。包括战斗模块在内，绿洲的所有系统都受到严重损毁，全部离线。", [270] = "刚才您执意接入系统，强制启动了战斗模块……\n尽管对心智造成了很大的损伤，但您依旧完成了。", [280] = "所幸强制启动只造成了短期的记忆混乱，您赌赢了。", [281] = "多亏了您，我们成功挡下了敌人的第一波攻势。但现在……", [290] = "突兀响起的通讯提示音打断了帕斯卡的讲解。", [300] = "接入通讯。", [310] = "这里是苏尔，预计2分钟后到达绿洲外环的预定坐标点。帕斯卡，教授还好吗？", [320] = "教授已经醒了，除了部分记忆数据混乱，一切体征正常。", [330] = "比预计用时少了一半，好强的恢复能力，不愧是教授！", [340] = "绿洲外环情况如何？", [350] = "情况非常危急……敌人的数量远比我们预估得多，绿洲外环的防御系统已经撑不住了！", [360] = "克罗琦呢？", [370] = "依照教授之前的指令，预计5分钟后与我汇合。", [380] = "至于伤亡情况……", [390] = "……尽管一路以来我们救下了不少同伴，但缺少成体系的战术指挥，我们还是……", [400] = "……教授，尽管您现在刚刚醒来，或许还处于混乱的状态之中。", [410] = "但就像之前无数次陷入危急关头的时候一样，我相信依靠您的指挥，我们就能渡过这次难关！", [420] = "在绿洲的生死存亡之际，我们会听从您的指令，倾尽所有进行战斗！", [430] = "教授，请下达<color=orange>指令</color>吧。", [-999] = ""}
