local AvgLang_cpt01_e_11_01_ZH_CN = {[10] = "罗萨姆扇区，管理员中心。", [11] = "bravo", [12] = "帕斯卡", [13] = "教授与汉娜达成一致，为罗萨姆扇区构筑了沙盒屏障。此后，罗萨姆与绿洲共同进退。而安冬妮娜则表达了自己对教授的不信任，就着怀疑的态度，暂时性地加入绿洲。", [20] = "苏尔到了，算量也是。绿洲的沙盒屏障已调满功率，都没什么问题了。", [30] = "你们那边怎么样？听说你们找到安娜了。", [40] = "她现在和帕斯卡一起帮助罗萨姆扇区的新管理员处理那边的善后事宜。", [50] = "新管理员？总之你们没事就好，尽快回来吧，苏尔现在一个劲地想去找你们，我都快按不住了。", [60] = "嗯，辛苦你了。", [70] = "> 通讯结束。", [80] = "通讯刚一结束，我就看到了从管理员中心走出来的她们。", [90] = "感谢各位的帮助，罗萨姆扇区将永远铭记你们于危难之中伸出的援手。", [100] = "你不打算和我们一起离开吗？", [110] = "新的图灵对现状一无所知，我不能抛下她。", [120] = "我从帕斯卡小姐那里得到了一条情报，您能开启一种可以覆盖整座扇区的屏障，对吗？那么罗萨姆扇区也……", [130] = "理论上来说，如果有扇区的<a href=Des:19>最高权限</a>的话，可以做得到。", [140] = "我已经从图灵那继承了罗萨姆扇区的最高管理权限，剩下的事就拜托您了。", [150] = "无妨，只是，开启沙盒屏障之后，罗萨姆扇区的立场将会和绿洲一样，无法再接受净化者的庇护。即便如此也无所谓吗？", [160] = "从信死亡那一刻起，罗萨姆扇区就没有其他的选择了。", [170] = "拿过扇区权限，我为罗萨姆扇区启动了沙盒屏障。", [180] = "> 沙盒屏障构建中，进度27%", [190] = "> 沙盒屏障构建中，进度64%", [200] = "> 沙盒屏障构建中，进度100%", [210] = "> 沙盒屏障已构建完毕。", [220] = "多谢，辛苦您了。", [230] = "我还有很多工作需要完成，就先不多打扰了，罗萨姆扇区永远欢迎你们。", [240] = "望着汉娜离开的背影，帕斯卡长舒一口气。", [250] = "总算是告一段落了，那么我们回去吧。", [260] = "在此之前我还有一个问题。", [270] = "什么？", [280] = "这个自称教授的家伙，究竟是谁？", [290] = "安冬妮娜，你在说什么？这不就是……", [300] = "这个人不是教授，帕斯卡你清醒一点！", [310] = "你不会是觉得教授长得不像教授才……", [320] = "云端上的外表依托于<a href=Des:47>心智投影</a>，人类的形象与现实不一这很正常，这我当然知道！", [330] = "我还知道这家伙的特征码和权限与教授完全一致！但这些都不是重点！", [340] = "你不该在我面前指挥作战的，真正的教授可做不到这么细致入微的指挥。", [350] = "够了！安冬妮娜！", [360] = "……帕斯卡？", [370] = "我说……够了，安冬妮娜。", [380] = "你不知道教授……你不知道<TA>为我们做了多少事情。", [390] = "我、苏尔、克罗琦……很多很多人形都是教授救下来的。", [391] = "是教授建立起了绿洲，是教授保证了大家的安全！", [392] = "……帕斯卡……", [400] = "……教授给了我们这些流亡者一个家……", [410] = "我们相信教授，不是因为教授是教授，而是因为<TA>做了值得我们相信的事情。", [420] = "就算这样……", [430] = "就算这样，如果没有教授，绿洲走不到今天。", [440] = "……你把话说到这个份上，是完全不容许我质疑了吗？", [450] = "……我不是这个意思，我只是想说，如果教授真的想要伤害我们，又何必等到今天？", [460] = "你不该为了一个疑点而全盘否定教授整个人呀。", [470] = "安冬妮娜皱着眉不满地撇了撇嘴，看得出来，她并不想跟帕斯卡争执。", [480] = "……我会盯着你的。", [490] = "最后，她将矛头指向了我，赌气似地丢下一句，旋即一个人走向了罗萨姆扇区的出口。", [500] = "……教授，请别生她的气，安冬妮娜她只是……", [510] = "我知道，安冬妮娜只是担心你们而已。", [520] = "而且，她还替你们问出了一直以来藏在心里的疑虑。这也是好事，不是吗？", [530] = "……其实并不是的，教授。", [540] = "自您将我从绝望中拉起那刻起，我就已经做好跟随您的准备。", [550] = "不论前方是什么。"}
