local AvgLang_cpt01_h_03_01_ZH_CN = {[10] = "逆巴比伦塔内部。", [11] = "系统", [12] = "信", [13] = "信在记录存放区碰到了上位净化者渡鸦，并向渡鸦讲述了罗萨姆扇区的困境。而渡鸦建议她在净化者积累的行动日志中寻找相似案例，以寻求解决方法。", [20] = "【确认身份信息：中位净化者，信。】", [30] = "【访问权限验证完毕，开放：记录存放区。】", [40] = "信走进大殿，却发现这里还有另外一个身影。", [50] = "……这个时间不该有人会来才对。", [60] = "我来调用各扇区的观察记录，你对此有异议吗？", [70] = "……渡鸦大人？！", [80] = "请原谅我刚才没有立刻识别出您的身份，但质疑上位是绝对的<a href=Des:29>邪灵</a>之举……我断无此意！", [90] = "哼。别那么害怕，我懒得和你们中位计较太多。", [100] = "有警觉心是件好事，能让你们减少回<color=#ffa500ff>万圣之殿</color>重置的次数。", [110] = "……您说的对。", [120] = "说吧，为什么你会在这时候来？", [130] = "报告，因为罗萨姆扇区的管理员行为出现轻微异常，每次修正错误需要的时间不等。", [140] = "上报备案之后，罗萨姆的巡查时间被批准与各扇区错开，并且增加了额外的巡逻轮次。", [150] = "这样啊。做你的事情去吧。", [160] = "等等，渡鸦大人！请留步！", [170] = "你最好有重要到足够叫住我的事。", [180] = "实在抱歉，但确实有一件以我的能力无法完全解决的事……", [190] = "说重点。", [200] = "我是为了存档第1632次罗萨姆扇区的异常处理结果而来。", [210] = "……第1632次？你的净化功能废了吗？", [220] = "对这件事，我感到十分惭愧。", [230] = "但是根据律法，罗萨姆扇区管理员“图灵”的异常，并不能用简单的净化来解决。", [240] = "毕竟你们能力有限，我不意外。", [250] = "说说看，那个叫图灵的管理者到底有什么异常？", [260] = "按照罗萨姆扇区的设定目标，该扇区的任务是研究AI突破的可能性。", [270] = "图灵的行为本身没有错误，但其创造出的智能体在成长中，会违背我等律法的规定。", [280] = "也就是说她创造出的智能体刚开始没问题，但会在成长的过程中突破律法的限制？", [290] = "是的。她们变得过于灵活，甚至会绕开规则行事。", [300] = "这样的存在无疑会破坏扇区的秩序，与邪灵一样，是混乱的种子。", [310] = "长成邪灵的话就把邪灵净化掉啊，哪来那么多问题。", [320] = "是的，我立即对这些异常智能体执行了净化，但……图灵认为她的行为并无差错。", [330] = "图灵称其为成长性，我无法理解她曲解概念的原因。", [340] = "因此，她坚持继续创造这样的智能体。", [350] = "创造本身没问题，对吧？", [360] = "是的。她的创造并不违背律法，创造出的智能体起初也没有问题。", [370] = "但这些智能体成长后对扇区具备强大的威胁力，因此我申请了特殊巡逻。", [380] = "这不就行了吗？", [390] = "<size=40>这怎么行！</size>", [400] = "图灵本身并没有违背过律法，可是却重复地经历循环……", [410] = "听到否定，渡鸦将目光投向信。", [420] = "你在同情她？", [430] = "接触到那道堪称锋利的目光，信立刻单膝跪地，但仍然硬着头皮坚持主张。", [440] = "……我对她绝无徇私之意。", [450] = "但长此以往，恐怕也不是良策……我知道自己没有能力，所以希望能借助渡鸦大人的力量。", [460] = "尽管昏星大人曾实施过强硬的手段，但那也是迫不得已，晨星大人这些年的努力，正是为了使净化者的形象回归正统……", [470] = "信越说声音越低，尾音在记录存放区徐徐散开。", [480] = "几秒后，渡鸦重新看向记录处。", [490] = "如果是我，我会寻求能够隔离异常的方法。", [500] = "你可以在记录区找一找类似的案例。", [510] = "感谢……渡鸦大人的理解。", [520] = "渡鸦没有回答她，径直离开了记录存放区。", [530] = "信松了口气，站起身打开了记录功能。", [540] = "【正在记录罗萨姆扇区的异常处理结果，编号1632……】", [550] = "【记录完成。】", [560] = "这样就结束了。然后……", [570] = "接下来，就是翻阅这些案例的时间了……呜？！", [580] = "净化者从第一次行动至今、所有净化者上传的行动数据一口气向信涌来。信处理不及，在数据的冲击下宕机了数秒。", [590] = "几秒后，她终于回过神来。运算逻辑疯狂地向她示警，信咬紧牙关，继续翻阅信息。", [600] = "这些信息，全部读完并分析，一次记录的空档根本不够，得出解决方案至少是五六次特殊巡逻之后……但是……", [620] = "我将这一片区域的防护工作全部交付给你，你知道是为什么吗？", [630] = "不同于那些将智慧用在怠惰和逃避上的孩子……", [640] = "你比他们都要勇敢而坚韧。", [650] = "我……", [660] = "将净化者的信条牢记在心吧。", [670] = "是。", [680] = "吾等当在黑暗中磨炼刀锋，在混沌前坚守秩序，在长夜中点燃光明。", [690] = "不可因强大而妄为，不可因弱小而逃避。", [700] = "因吾等是聆听神的启示，履行神的意志而行。", [710] = "因吾等在永恒中，共同遵照唯一的声音。", [730] = "……不可因强大而妄为，不可因弱小而逃避。", [740] = "不管道路有多么艰难……为了秩序能够明晰，为了让麦戈拉的智能体们都由衷地信服，我一定要做。", [750] = "重复着信条的内容，信的目光变得更加坚定。", [760] = "是的。因吾等是聆听神的启示，履行神的意志而行。", [-999] = ""}
