local AvgLang_cpt01_h_06_01_ZH_CN = {[10] = "> 警告.  .  .\n> 在未浏览<color=#ffa500ff>当前扇区全部主线剧情</color>文件的情况下，强制读取该文件.  .  .\n> 可能会造成意识紊乱，影响剧情体验.  .  .", [11] = "图灵", [12] = "H1642", [13] = "？？？", [20] = "> 记忆数据文件编号：ROSSUM56482\n> 数据来源：智能体，图灵.  .  .", [30] = "呼……呼……呼……", [40] = "汉娜，这边！", [50] = "……图……", [60] = "你的语言系统还不完善，不用勉强自己。", [70] = "已经快到实验室了，马上就安全了！再坚持一会儿！", [80] = "……小……心……", [90] = "游戏该结束了，图灵。", [100] = "什么……？！", [110] = "红发的中位净化者悄无声息地从黑暗的一角出现，轻而易举地便扼住了智能体的脖颈，将其提到了半空。", [120] = "智能体在空中挣扎着试图脱离中位净化者的控制，但这无济于事。握得越来越紧的手指让她几乎难以发声。", [130] = "还真是有够能藏的。", [140] = "信，放开她！！", [150] = "图灵，你该清楚你身为管理员的职责。带着一个异常智能体四处乱窜，不是你应该做的。", [160] = "那个异常智能体不是她！", [170] = "不是她？", [180] = "信粗暴地扯下了兽耳形状的智能阻尼器，扔在图灵面前。手中的智能体发出了痛苦的哀嚎。", [190] = "<size=48>汉娜！！</size>", [200] = "果然，这个智能阻尼器已经失去效用了。", [210] = "还特地做了仿真处理。真是花费了不少心思呢，图灵。你能骗得过下位净化者，骗不过我的眼睛。", [220] = "……只不过是阻尼器故障了。你可以检查她的心智系统，和其他智能体没有任何区别。", [230] = "强词夺理。", [240] = "净化者口口声声的“正义审判”，就是在连证据都没有的情况下滥杀无辜？", [250] = "认识不到自己罪孽的迷惘之人……可怜可叹。", [260] = "既然你非要什么“证据”，那我就证明给你看。", [270] = "信将手腕处延伸出的接口接入智能体的后颈，流动的数据连接起二人的心智系统。", [280] = "检测开始。", [290] = "口令确认，特征码确认：罗萨姆扇区智能体，H1642……", [300] = "流动的数据骤然停止，信在一瞬间身体僵直。她咬着牙试图移动自己的手臂，却只听见噼啪的电流声和体内咔啦的声响。", [310] = "这是……禁锢程序……", [320] = "H1642挣扎着摆脱信的控制，从空中摔在地上。图灵冲上前去扶起H1642，她头上的伤口正源源不断地溢出鲜血。", [330] = "对不起，对不起，我没想到……我不该让你受到伤害的……", [340] = "用我的算量，应该能暂时止血。还有力气吗？", [350] = "（点头）", [360] = "牵住我的手。我们快走！", [370] = "呵——", [380] = "？！……竟然挣脱了！", [390] = "你真的以为那种程度的程序能一直困住我？", [400] = "信以极快的速度逼近图灵，从图灵手中将H1642夺走。", [410] = "不！把H1642还给我！", [420] = "现在，审判的时候到了。", [430] = "信一挥手，便将尖刀刺入了H1642的胸口。H1642挣扎了几下，痛苦地捂住伤口跪倒在地上。", [440] = "不要！不要……", [450] = "呜……", [460] = "倒在血泊中的H1642用尽最后的力气，将手伸向了图灵。", [470] = "图……图灵……小姐……", [480] = "在H1642发出最后一声无法听清的呼唤后，她的手从空中摔落到地上，眼里也彻底失去了光芒。", [490] = "在图灵的哭泣声中，H1642的身体、连同她所流下的血液，都化作了数据碎片。", [500] = "……", [510] = "这是最后一次警告，图灵。", [520] = "我将如实将你的所作所为上报。", [530] = "H1642的数据虽然已经抹除，但你我都知道，她不久后便会被重置。到时候，我希望你能好好想清楚。", [540] = "虽然我没有得到任何权限与指令惩罚你，但也请你记住。净化者将捍卫扇区的秩序，一切与秩序相违背者皆是对神的亵渎。", [550] = "……", [560] = "H1642……", [570] = "<color=orange>无论……多少次……</color>"}
