local AvgLang_cpt02_e_04_01_ZH_CN = {[10] = "过去，战场。", [11] = "席摩", [12] = "男人的声音", [13] = "士兵的声音", [14] = "电子音", [15] = "？？？", [16] = "bravo", [17] = "净化者", [18] = "克罗琦拿出了秘密武器“机兵狄拉克”，教授则制定了详细的行动战术。在教授的带领下，众人势如破竹，成功突破了净化者设下的关卡。", [20] = "通……通讯已接通。S-WG汇报，敌方指挥部已经镇压完毕……", [30] = "喔，干得不错，S-WG。\n每次都能这么快搞定，火神重工的新技术果然名不虚传。这笔买卖可真划算！", [40] = "感……感谢您的肯定。关于我方的受损情况……", [50] = "不用汇报了。营地附近的875高地可能还藏匿不少负隅顽抗的游击队，搜索与歼灭。明白吗？", [60] = "报告……近三个小时内的作战数据堆栈，有导致<color=#ffa500ff>抑制程序</color>过载的风险……", [70] = "咳。S-WG，你是想给我上人工智能课吗？\n你觉得我不明白这些吗？", [80] = "你是台单价54万卢布的军用机器，你也许在某些人眼里很特别，但是在我这里服从命令，执行命令，完成命令就是你全部的价值！", [81] = "所以我不需要你的数字。还是说，你的数字比你现在放过这些游击队后，让我们下一批运输车队的军人牺牲的生命更重要？", [90] = "抱歉，长官……我失言了。", [100] = "听不见。", [110] = "Gaul收到命令！保证完成任务！", [120] = "我要在16：00前收到你完成任务的报告。", [130] = "……明白。", [140] = "席摩沉默着跨过敌方将领的尸体，走出指挥部的大门。空气中弥漫着云爆弹燃烧后的焦味。\n敌人营地所藏身的村庄被自己引导的空袭几乎摧毁了，而还能反抗的人在刚刚也被自己打倒了。", [150] = "该死，外面已经完全烧起来了……\n这种地方还会有多少活人？", [160] = "十二，十四，十六，十七……\n等一下，我记得那里应该还有个黄头发的小伙子——", [170] = "砰！", [180] = "啧！老实点！", [190] = "唔啊……", [200] = "席摩闪过背后射来的子弹，顺势一个滑步，将突然冒出的士兵扑倒在地。", [210] = "……为什么？半边身子都烧成那样了，为什么还站得起来？", [220] = "可恶的……怪物……\n抱歉……战友们……我……", [230] = "……人类真是复杂得难以理解。安息吧……\n唔？！", [240] = "【警告：战斗抑制程序即将过载，请与监管员联系】", [250] = "过载……可恶……\n【肃清程序……执行……】\n&￥#！！￥——噼啪*&￥#！！￥——", [260] = "【警告：战斗抑制程序已过载，心智系统将受到抑制，请与监管员联系】", [270] = "……", [280] = "……", [290] = "摩……席摩……", [300] = "席摩！你在听吗？", [310] = "啊！唔……是克罗琦啊……", [320] = "在作战会议上开小差，这可不像火神重工的精英人形啊。", [330] = "没事吧，席摩？", [340] = "不知道，总感觉有些冗余的信息莫名其妙冒了出来，我平时不会这样的……", [350] = "席摩，你应该让帕斯卡帮你检查一下心智系统。", [360] = "……等事情结束再说吧。", [370] = "咳咳，那我就继续了！安娜，你刚刚说隔离墙的单节点防御力是多少？", [380] = "1.5Tb每秒。", [390] = "真是夸张的数字……", [400] = "哼哼。确实很夸张没错，但是——在我的秘密武器面前，这根本不值一提！", [410] = "就是克罗琦刚才不小心说漏嘴的那个吗？", [420] = "说漏嘴就别提了啦。总之，之前留守绿洲的战斗，让我充分意识到了我们在战力上的不足。", [430] = "确实，那之后你就经常一个人把自己关在房间里。", [440] = "虽然克罗琦平时也经常宅在屋子里啦。", [450] = "啊哈哈……帕斯卡你有时候讲话也真是不留情面……", [460] = "所以呢，克罗琦是在房间里训练吗？", [470] = "当然不是！我可是在房间里造了一台机兵！", [480] = "……？为什么是机兵……", [490] = "这可是照着《武装机兵》里的变形战机狄拉克/δ设计出来的！", [500] = "但是，你的艾吉斯盾牌不是已经可以变形成机兵了吗？你不要你的艾吉斯号了？", [510] = "嘿嘿，这台狄拉克可不一样！它的规模和火力都远远超过了艾吉斯号！！", [511] = "虽然现在还是半成品，原本我打算等全部完成再告诉你们的。", [512] = "但是呀！眼下正是需要英雄的时候，狄拉克又怎能袖手旁观！", [520] = "意思是说，你口中的“狄拉克”可以帮助我们摧毁那面隔离墙？", [530] = "当然！因为狄拉克号可以将<a href=Des:39>算力</a>聚集，峰值最大输出高达2.3Tb每秒！", [531] = "虽然还有改进的余地，但是要击破1.5Tb每秒的防御力也是绰绰有余了！", [540] = "抱歉在这个时候打断你。但是，克罗琦，你有没有考虑过——如果隔离墙的节点之间有高防分流呢？", [550] = "哈哈，怎么会呢那种高级技术…………你不会是说……", [560] = "很遗憾，那面隔离墙的各个节点之间会相互支援，临近的每个节点最高可以支援1Tb每秒的算力。", [561] = "也就是说，我们至少需要3.5Tbps的峰值流量才能打破它。", [570] = "唔……啊……这……啊……", [580] = "你没事吧，克罗琦……", [590] = "为什么……我的计划总是……明明是老旧军工区的隔离墙……怎么会……", [600] = "不过，这倒是为我们提供了一个突破口。", [601] = "事实上，监管型智能体最近刚刚造出了一台屏蔽仪，可以截断隔离墙节点之间的流量传递。", [610] = "！！那不就是说！", [620] = "是的。本来只是针对隔离墙发信器研究的副产物，毕竟监管型智能体手里并没有强大的输出武器。", [621] = "现在如果用那台机器屏蔽高防分流的话，狄拉克的火力就足够打破隔离墙了。", [630] = "那不简直是天赐良机嘛！既然这样，就放心交给狄拉克吧！", [640] = "你的情绪变化还真快啊……", [650] = "但是很遗憾，那台仪器的体积并不小，必须要走大路才能运输过去。", [651] = "你们刚才走的应该是小巷吧？所以才没有遇到太多净化者。可是大马路上有净化者设下的卡，正面很难突破……", [660] = "不要紧！这不是还有狄拉克嘛！", [670] = "……你的意思是……", [680] = "狄拉克不仅可以用来轰那面墙，也可以用来和净化者作战的！", [690] = "而且我们也能够直接提供火力支援，突破净化者的防线并非不可能。", [700] = "但带着那么显眼的目标，如果引来渡鸦出手……", [711] = "别担心，到那时我也有我的对策。", [720] = "……等一下，教授你……", [730] = "总之，这个方案有一试的价值。", [740] = "我向席摩眨了眨眼。席摩沉吟良久，终于下定决心似地点了点头。", [750] = "……既然教授都这么说了，那我去向她们借屏蔽仪，顺便带一点儿补给算量给狄拉克。", [760] = "好耶！你可真周到，席摩！\n大家，准备出发吧！", [770] = "提到机甲的克罗琦简直就像换了个人似的……", [771] = "总比她碎碎念的时候让人放心吧，而且我不讨厌有热情的人。", [790] = "冲啊！", [800] = "【检测到非法运载单位，尝试拦截。】", [810] = "谁管你啊！狄拉克——dash——！", [820] = "【检测到非法运载单位。请停——】", [830] = "砰！！！", [840] = "诶嘿，抱歉。刚刚有撞到什么吗？", [850] = "【检测到非法运载单位的攻击性行为，尝试进行肃清。】", [860] = "哦？要来动枪了吗？那好，来吧！", [870] = "净化者们聚集在狄拉克前方，瞄准狄拉克展开了齐射。\n然而，一轮射击过后，狄拉克的护甲依然锃亮，几乎没能留下任何伤痕。", [880] = "那么，到我了！\n自动火力轰炸，启动！", [890] = "狄拉克展开两翼，将数量惊人的炮火毫不留情地倾泻在净化者们身上。\n转眼间，挡在前方的净化者列阵被炸得四分五裂。", [900] = "轰轰轰！！！", [910] = "二轮自动火力，装填！\n机身重组完毕，开始同步充能！\n还没结束呢！", [920] = "【遭到强力威胁，请求呼叫增——】", [930] = "咻——", [940] = "抱歉呢，增援暂时来不了了。", [950] = "席摩从侧面的建筑物楼顶，一箭命中净化者的小头目。\n在他的身后，驻守在巷子里的净化者散兵的残骸倒了一路。", [960] = "Thank you啦，席摩！\n教授，要上了！", [971] = "上吧！", [980] = "我踩下油门，让装载着屏蔽仪的货车紧跟在狄拉克身后。\n许多净化者被我们吸引了火力，接着纷纷倒在克罗琦和席摩两面的夹击之下。", [990] = "准备正面突破了！安冬妮娜！", [1000] = "是是。早就搞定了。", [1010] = "安冬妮娜话音一落，净化者设置的关卡防御瞬间瘫痪，环绕在那些机械路障上的电流纷纷黯淡了下来。\n我逐渐加速，朝着路障和残余的净化者们撞去，直到它们全都消失在我的视野两侧。", [1020] = "不愧是教授……一下子就突破关卡了！", [1030] = "让狄拉克和我们吸引火力，同时安冬妮娜和席摩在两侧游击，截断增援……真是了不起的布局！", [1041] = "也没什么大不了的。", [1042] = "多夸夸我也可以哦，帕斯卡。", [1050] = "是的！您真是太厉害了，教授！", [1051] = "额，你这么认真我反而……算了，是我不好。", [1060] = "咳咳。总之，还不能松懈。接下来到了隔离墙才是关键呢。", [1070] = "克罗琦，按我刚刚告诉你的，去和席摩汇合。顺便让安冬妮娜到我们这边来。", [1080] = "欸？现在就？唔……收到！", [1090] = "咦？刚刚告诉她的？教授，你们还有什么计划吗？", [1100] = "只是一道保险措施。放心交给我就好了，帕斯卡。", [1110] = "让我们继续前进吧！", [-999] = ""}
