local AvgLang_cpt02_e_05_01_ZH_CN = {[10] = "没想到靠近隔离墙的地方还有这么多净化者……", [11] = "bravo", [12] = "？？？", [13] = "帕斯卡", [14] = "上位净化者渡鸦出现在隔离墙前，展现出了强大的实力。然而，在教授和席摩的配合下，渡鸦的攻击反被利用，成为打破隔离墙的契机。", [20] = "教授，接下来……", [30] = "加快速度，我们得尽快到达目标点。", [40] = "你有什么对策么？", [51] = "解释起来需要时间，总之我们先到那里再说。", [52] = "包在我身上，你们就看好了吧！", [60] = "好，我相信您的判断！", [70] = "……这可不是在开玩笑。", [90] = "到了！", [100] = "又见到这面隔离墙了……心情还是一如既往的压抑啊。", [110] = "……？等一下，教授，你这是把车往哪儿开呢？", [120] = "（α 53.44，β 42.11，γ 44.15）。", [130] = "这又是哪的坐标？怎么听上去像是要往巷子里挤……\n喂，别开玩笑了！你还真的拐弯啊！", [140] = "教授，您……", [150] = "别急！就在这里，我们先下车。", [160] = "哈？", [170] = "前面就是渡鸦的领地了，我们得换个地方侦查情况……", [180] = "没有那个必要了吧？反正不管到哪里，你们都快死了。", [190] = "！！", [200] = "粉发的上位净化者从上空缓缓落下，转瞬之间便到了我们眼前。她的面容被半透明的面罩遮盖，看不清表情，但强大的气场已经镇住了在场的所有人。", [210] = "那个该死的莉维雅可骗不过我。我就知道基洛普斯扇区有邪灵混进来了。", [220] = "不过没想到你们居然能一路杀到这里来，下位的那些家伙真是不中用啊。", [221] = "你们能给我带来怎样的战斗呢？我还挺期待的呢。", [230] = "你就是渡鸦？", [240] = "你们这些虫子可没资格叫我的名字！", [250] = "不等安冬妮娜回话，渡鸦手中的长枪已然飞出，直冲她而去。", [261] = "危险！", [262] = "（扑开安冬妮娜）", [270] = "……！！", [280] = "安冬妮娜一个趔趄跌倒在地。下一秒，她身旁的地面已经被飞枪炸出一个坑。", [290] = "……！！", [300] = "我抱着安冬妮娜在地上滚了两圈，再睁开眼时，她刚才站立的地方已经被飞枪炸出一个坑。", [310] = "嚯。真是蹩脚的动作，简直让我失望透顶。", [320] = "不过，也真亏你能预测到我的攻击方向呢。不对，难道不是预测吗……", [330] = "没事吧！", [340] = "……没事。这个家伙很危险……", [350] = "安冬妮娜刚从地上爬起来，渡鸦就再度抬起了手。", [360] = "糟了！她又要攻击了！", [370] = "教授，快闪开！", [381] = "别慌，她瞄准的是后面！", [390] = "我们先走另一条路逃跑！别被她拖住了！", [400] = "欸？什——", [410] = "轰！！", [420] = "渡鸦的下一次攻击并没有指向我们，而是干脆利落地朝向了运载屏蔽仪的货车。\n随着一声巨响，我们辛苦运来的屏蔽仪轻而易举地被火海吞没。", [430] = "怎么会……这下我们的计划……", [440] = "不要管了！先走！", [450] = "我拉着帕斯卡和安冬妮娜，跌跌撞撞地逃进了路旁的废弃大楼里。身后传来渡鸦的喃喃自语。", [460] = "第二次了……连续两次都被预判到了，果然不是巧合。", [461] = "按规定，或许应该上报给“晨星”大人，但如此特殊的战斗机会……", [470] = "你们觉得我会放过吗？这可是我为数不多的乐趣呢……哈哈哈哈！", [490] = "教授……您这是？", [501] = "别着急，我们还有反击的机会。", [510] = "这里可是楼顶，你真的觉得没问题吗？万一那个净化者能飞……", [520] = "真是慌不择路啊，虫子的智商也就是这样了吧。", [530] = "……该死。我怎么跟克罗琦一样乌鸦嘴了……", [531] = "<size=40>？</size>", [532] = "克罗琦", [540] = "渡鸦的身影自大厦之间缓缓升起，睥睨着缩在屋顶的我们。", [550] = "在我处死你们之前，我还有最后一个问题。", [560] = "“教授”吗……呵呵。你居然真的能看清我攻击的轨迹。", [570] = "你拥有的算量很庞大呢，难道是他们的领导者？", [581] = "没错！很惊讶吗？", [582] = "怎么，我还以为你对“虫子”没兴趣呢。", [583] = "……", [590] = "承认得真痛快。那么，我就和你好好玩玩吧。", [600] = "你说得对，我也没兴趣在你们这些小虫子上面浪费心思。", [610] = "毕竟马上就要说再见了。", [620] = "不回答吗？真可惜，难得我有兴趣给小虫子一个留下遗言的机会。", [630] = "渡鸦轻笑了一声，手只是轻轻地一挥，长枪立刻从下方升起，飞回她的手中。", [640] = "又要来了，大家快找掩体！", [650] = "紫色的光芒在她的枪柄闪烁，随着长枪转动，光芒逐渐积聚。仅仅凭肉眼观测，瑟缩在掩体后面的我们就能感受到这一击的能量多么巨大。", [660] = "就在她长枪即将脱手的一瞬间，我意识到时机已到。", [671] = "就是现在！散开！！", [680] = "“轰——”", [690] = "刹那间烟尘四起，零点几秒前我们所在的位置已被长枪穿过，以此为中心，裂缝层层扩展，剥落的墙面因强大的冲击力浮在空中，仿佛整个扇区都在震动。", [700] = "教授！安冬妮娜！大家都还好吗！", [711] = "我没事。", [720] = "太好了，我这边也——", [730] = "这就放心了？未免也太天真了。", [740] = "烟雾再度沸腾起来，在空中旋转着又簌地散开，光凭空气的流动我们便本能地感受到恐惧。", [750] = "该死！她还有一柄长枪！", [760] = "没有时间做出更多的反应。视野恢复清晰的同时，长枪也已逼近。虽然所有人分散在不同的地方，但渡鸦的长枪对准的只有一人，便是——", [770] = "教授！！", [780] = "将自己的生命作为赌注并非第一次。是的，我必须承认：即便死亡如此之近，近到我能清晰地听见心脏鼓动的声音混杂在冷风的呼啸间，我还是在期待这一刻的到来——如同将棋子落在棋盘的时刻。", [790] = "这一次，棋局的结果又将如何？", [800] = "“啪！”", [810] = "——最后一颗棋子落下。", [820] = "将军。", [830] = "什么？！", [840] = "来自远方的箭矢精确地击中了高速飞行的长枪，尽管对于长枪来说，那只不过是微不足道的一点力量，丝毫不能削弱其威力，但却足以让其些微地改变飞行的轨道。", [850] = "而这些微的改变，便是胜利的关键——", [860] = "——克罗琦！开炮！", [870] = "明白！", [880] = "巨大的机兵从天而降，凝聚的光束从其双臂的炮口轰然射出，如同流星一般在空中划出闪亮的弧线，紧随渡鸦的长枪而去，仿佛带着无数人的执念，最终，射入了那面横亘基洛普斯扇区的巨大的透明屏障。", [890] = "“咔——咔嚓——”", [900] = "清脆的碎裂声划破基洛普斯扇区的天空。", [910] = "成功了！打开了一个缺口！", [911] = "席摩", [912] = "克罗琦", [913] = "渡鸦", [920] = "还没完呢！尝尝这个！", [930] = "脉冲——轰击——", [940] = "渡鸦还没回过神来，克罗琦的炮火已经对准了她。她下意识地挥手，试图呼唤长枪回到手中，抵挡攻击。然而——", [950] = "……该死！", [960] = "刚才投出的长枪，已经在命中隔离墙的同时化作灰烬，还来不及再生。意识到这一点后，渡鸦匆忙想要改用另一只长枪招架。然而，那仅仅零点几秒的犹豫，已经让她失去了防御的机会——", [970] = "<size=40>呜啊！！</size>", [981] = "击中了！就是现在！", [990] = "上！反击的时候到了！", [-999] = "粉发的上位净化者从上空缓缓落下，转瞬之间便到了我们眼前。她的面容被半透明的面罩遮盖，看不清表情，但强大的气场已经镇住了在场的所有人。"}
