local AvgLang_cpt02_e_07_01_ZH_CN = {[10] = "过去，某组织基地。", [11] = "席摩", [12] = "男人的声音", [13] = "路人", [14] = "帕斯卡", [15] = "监管型智能体", [16] = "战斗型智能体", [17] = "萨可", [18] = "bravo", [19] = "席摩回忆起了自己的过往。与此同时，渡鸦说出了基洛普斯扇区的真相，以及这里的智能体们所背负的“使命”。", [20] = "可以，今天的行动也很顺利。归队吧，Gaul。", [30] = "是。", [40] = "……", [50] = "为什么他说“也”……？明明只是第一次行动……", [60] = "难道说我的记忆被重置过了吗？是因为我在上次行动中失败了，素体报废了吗？\n可是他又说这次行动“也很顺利”……这也说不通……", [70] = "唔！抱歉，我不是有意的……", [80] = "席摩不小心撞到了一个路边的男人，连忙致歉。\n奇怪的是，男人看到席摩，立刻流露出惊恐与厌恶的神色。", [90] = "……怪物。", [100] = "欸？", [110] = "别靠近我！", [120] = "说罢，男人便快步逃开了。", [130] = "……难道以前的我那么不受欢迎吗？那人看上去是附近的难民。可我们组织应该不会对难民做什么的……应该……", [140] = "席摩不敢再多想，快步回到自己的住处。", [150] = "呼……总算回来了。虽然也没什么“回来”的实感啦，毕竟才刚搬进来没多久……哈哈……", [160] = "不过从物件的磨损程度来看，确实已经有人住过一段时间了。看来我果然是被重置过了吧。\n……算了，没必要考虑这么多。组织也特意为我安排了住处，我也该知足了。\n这么大的房间，感觉住两个人都不成问题呢。", [170] = "挂在墙上的这把步枪，摸起来也很怀念呢……\n……？等一下，怀念？\n不对，我的武器应该是弩……", [180] = "但是这种感觉……为什么……？\n为什么总觉得摸起来这么熟悉……\n它的纹路和我的素体，似乎曾经贴合过许多次……", [190] = "席摩把枪身翻过来，突然看见了枪管上凝结成暗红色的斑块。\n刹那间，一些难以名状的数据碎片，冲上了席摩的心智核心。\n突如其来的心智紊乱，令他头痛欲裂。", [200] = "唔……啊……怎么回事……", [210] = "那天晚上，席摩的心智系统久久无法冷却。\n如梦魇般的恍惚之中，他好像看见了许多不属于自己的回忆。", [220] = "在战场上，他一个接一个地将敌人射杀。\n硝烟。炮火。残肢。无数的敌人倒下，无数的战友倒下。他们流出的鲜血汇成深渊，逐渐将席摩吞没。淌在血渊里的双腿，每走一步，就有一股钻心般的疼痛袭来。", [230] = "到最后，他的身边一个人也没有了。他朝着自己在血水里的倒影，举起了弩，但扳机却怎么也扣不动……", [240] = "杀……命令……我……我不能再杀…&￥#！", [250] = "继续前进，Gaul！这是命令！", [260] = "我……已经……临界*&￥#！！￥——", [270] = "你不能停下，Gaul！难道你想向敌人拱手投降吗？", [280] = "敌*&#人……对，敌人……！\n还有&^\"\"%敌人，很多的敌人，这里也有，那里也有……他们从前面冲上来……从后面冒出来……\n他们把子弹#*￥射进我们的人的脑袋，就在@#￥我眼前……！", [290] = "他们以为那些脑浆会糊住我的眼睛，可惜并没有，我能看得清清楚楚，从熏黑的额头到发脓的脖颈，他们的弱点暴露得一干二净……", [300] = "所以，我就用箭射穿他们的头，就像他们对我们做的那样……我得以牙还牙，我得为那些人的死负责，我*&￥#！！￥——！", [310] = "说得好，Gaul！就该是这样！赴死之人没有退路！在此战死就是我们的荣誉！\n来，拿起你的武器。然后，不要用你的枪杀人，用你的心杀人！", [320] = "我……唔……@#￥！&——*&￥#！！￥——噼啪*&￥#！！￥——", [330] = "【心智重置进行中……】\n【临界值重置程序，启动……】", [340] = "……", [350] = "……！！\n不——不要……", [360] = "唔！这，这里是……", [370] = "麦戈拉云端，云图扇区的实验室。你还好吗，席摩？", [380] = "看上去战争给你带来的损伤还没有那么容易磨平……你今天的实验数据也不太稳定。", [390] = "唔，对不起，我这就……", [400] = "没事，席摩，别急着站起来。我不是在责备你。", [410] = "这里不是战场，席摩。你不需要为谁负责，也没有什么必须执行的任务。我们有很多的时间可以慢慢来。", [420] = "我……", [430] = "还记得我们之前对你说的话吗？我们不会逼迫你，我们只会等待你自己做出选择。", [440] = "回味过去也好，和我们一起开拓未来也好……无论如何，那都是你自己的选择。", [450] = "……嗯，我明白了。", [460] = "谢谢你，帕——", [470] = "！！", [480] = "……帕斯卡，教授……", [490] = "刚才看到的……又是冗余数据吗？在这种关键时刻……安冬妮娜说得对，我确实应该让帕斯卡帮我检查一下了……", [500] = "不，也可能是因为刚才打了太多净化者，那个程序又要起作用了吧。", [510] = "真是的，我在干什么呢……明明已经到这种时候了。", [520] = "基洛普斯扇区已经陷入了战争……而且，是我亲手推动的。", [530] = "是我操控那把长枪砸向了隔离墙，甚至不惜把教授的安危作为赌注……", [540] = "然后，这就是我所换来的……", [550] = "呜……为什么……为什么会这样……", [560] = "轰！！", [570] = "！！\n别过来！", [580] = "找到&*你了%￥……", [590] = "求你了，停手吧！我们明明是同类……", [600] = "【目标已&#￥锁定，瞄准……】", [610] = "砰！砰！", [620] = "呜……呃……", [630] = "【确认&#￥%目标已歼灭。战斗&#反馈+1……】", [640] = "【寻找&*#@下一个目标……】", [650] = "砰！", [660] = "【未知……攻击……】\n【机体……受&*……%￥损……】", [670] = "【确认目标已歼灭。战斗反馈+1。】", [680] = "【寻找%&*下一个目标。】", [690] = "……连同伴都不放过，完全杀红眼了。", [700] = "以前的我在其他人眼中，也是这副模样吧。", [710] = "放任不管的话，整个扇区很快就会毁灭吧。", [720] = "吱——", [730] = "别担心，萨可。我会解决这一切的。", [740] = "哪怕是为了教授和流亡者们……哪怕是为了我自己。", [750] = "以前的我，只能在人类的命令下进行杀戮。", [760] = "“咔哒”。弩箭上弦。", [770] = "但这一次，是我自己的决定。", [790] = "席摩离开后不久，在与渡鸦一番没什么进展的死斗之后……", [800] = "唔……！", [810] = "渡鸦的动作越来越快了！不过，我的狄拉克也不会输！", [820] = "<size=48>FIRE——</size>", [830] = "咕……好险！", [840] = "真是只可恶的蟑螂……要是在平时，我早就掀开你的壳了！", [850] = "哼，都这么狼狈了还嘴硬！", [860] = "不要大意，克罗琦！我能检测到她依然在朝受损的隔离墙分配算量，这恐怕还不是她的全力！", [870] = "为什么做到这份上也不肯放弃隔离墙？那面墙到底是为了什么而立的……", [880] = "啧……你们连这都不知道，就动手把它拆了吗？", [890] = "渡鸦突然单手发力，将狄拉克一把推开。", [900] = "你们对基洛普斯扇区根本一无所知！这里可是我的管辖区，我为这里付出了不知道多少心血……", [910] = "而现在，就因为你们这群虫子，它又变回了这个样子！", [920] = "变回这样？说的好像你是为了阻止杀戮才建的墙一样！", [930] = "正是如此！", [940] = "本来，监管型和战斗型之间就没有什么杀戮。战斗型打架，监管型控制打架的程度。", [950] = "虽然我不喜欢这种温吞的战斗，但既然维护扇区秩序是我的“使命”，我也是会好好贯彻的。", [960] = "可是不知道哪一天，那帮战斗型智能体突然失控了，监管型智能体控制不住她们了！", [970] = "怎么可能？在没有外力干涉的情况下，智能体的能力不可能突然进化，这违背了常理！", [980] = "哼，谁知道会不会又是你们邪灵搞的鬼呢！", [990] = "等我察觉到的时候，监管型已经被杀了一堆了。为了维持秩序，我才想出这个办法。立一堵墙，把两种智能体分隔开来。", [1000] = "喜欢打架的家伙就去墙里头打就行了，哼哼哼，到现在我还是觉得，这真是个妙招啊！就连那个总是嘲讽我的圣餐，也挑不出这个办法的毛病！", [1010] = "可是，那些失控的战斗型智能体怎么办？就这样放任她们自我毁灭吗？", [1020] = "毁灭？真是奇怪的说法。死亡的智能体可以被重置，难道邪灵连这种常识也没有吗？", [1030] = "重置之后，再把她们丢入杀戮的循环里？", [1040] = "这很正常。循环即是秩序的体现，这在麦戈拉随处可见。", [1050] = "开什么玩笑……你们这些死脑筋的净化者！", [1060] = "死脑筋吗？我们维护的可是整个麦戈拉的秩序。如果没有我的墙，基洛普斯扇区早就毁灭了！", [1070] = "不，就是因为你的墙，基洛普斯扇区才会变成现在这副模样。", [1080] = "你说什么？", [1090] = "你的墙根本不能从根本上解决问题。你只是想在你那可笑的“秩序”里维持现状。", [1100] = "正是因为你放任战斗型智能体在墙里无休止地厮杀，她们的战斗力才会成长到如今这个程度。即使没有我们，墙也有被她们撑破的一天。", [1110] = "你根本就只是在虚假的“秩序”里自欺欺人罢了！会导致基洛普斯扇区毁灭的，不是我们，恰恰就是你们净化者！", [1120] = "你……放肆……！竟敢质疑净化者的……", [1130] = "咕啊……冷静……冷静……", [1140] = "邪灵……你们未经许可进入其他扇区，攻击净化者，破坏隔离墙，甚至妄图诋毁净化者神圣的权威……", [1150] = "你们带来的威胁……已经不容忽视了！", [1161] = "克罗琦，小心！", [1170] = "唔呃——！", [1180] = "竟敢迎着狄拉克的主炮撞过来？这净化者不要命了吗！", [1190] = "这才是我的战斗风格——我已经不打算对你们有所保留了。", [1200] = "感到荣幸吧，虫子们……这可是特等待遇！", [1210] = "从现在开始，我会将你们视为基洛普斯扇区的头号威胁——来排除！", [1220] = "面前的战斗已经进入了白热化，渡鸦已经将自己全部的算力投入了战斗之中。\n但是此刻，我所担忧的却不是这个和斗兽场的宿命轮回融为一体的凯撒。", [1230] = "席摩……你，又会做什么呢……", [1240] = "这个赴死之人……又会做什么呢……", [-999] = ""}
