local AvgLang_cpt02_e_08_01_ZH_CN = {[10] = "基洛普斯扇区，监管型智能体工作中心。", [11] = "？？？", [12] = "bravo", [13] = "塔莎", [14] = "莉维娅", [15] = "莉维雅本想通过杀死战斗型管理员塔莎结束战争，却反而被逼入绝境。关键时刻，席摩赶到，救下了莉维雅。", [20] = "呼……呼……", [30] = "果然……比以前强了很多呢……", [40] = "塔莎……", [50] = "一阵爆炸声从走廊的尽头传来。莉维雅知道，另一侧的出口已经被战斗型智能体攻占了。", [60] = "看起来这栋楼也快撑不住了。", [70] = "居然在这种狭小的地方使用爆破武器，战斗型的那帮家伙也太乱来了……", [80] = "不对，应该说这就是她们的“使命”吗？只要能够造成更多的破坏就可以了，别的事情她们都不会考虑……", [90] = "真是疯了——", [100] = "……不对。错的不是她们。是我们，我们没有尽到监管型的职责。", [110] = "身为监管型的管理员，我必须阻止……必须阻止她们……", [120] = "光凭你又能阻止谁呢？莉维雅。", [130] = "……塔莎？", [140] = "你追得可真紧啊……就那么喜欢粘着我吗？一定是因为太久没见面，想我了吧，哈哈……", [150] = "别再嬉皮笑脸了。我是来和你做个了断的。", [160] = "腿部中了两枪，腹部中了一枪。你是怎么回事，莉维雅？和以前相比，你变弱太多了。", [170] = "是吗……就算变弱了，我也是你的监管员呢。你的每个动作，每个习惯，我都非常了解。你杀不掉我的，塔莎。", [180] = "和自己的监管员近身作战的数据，非常珍贵。我不会手下留情的。", [190] = "那，你试试看？", [200] = "塔莎没有回话，而是迅速弓起身子，拔出腰间的匕首。下一秒，她已经冲刺到莉维雅身前。然而——", [210] = "……抓到你了。", [220] = "！？", [230] = "莉维雅侧身一闪，反手抓向塔莎伸出的手臂，试图借着冲刺的速度将她向前拉倒。\n意识到事情不对，塔莎立刻将匕首反握，在两人之间一划，逼得莉维雅将手缩回。", [240] = "随后，她一个后空翻，从莉维雅身旁逃开。在半空中，某处传来按钮被摁下的声音，混在风声里传入她的耳朵。\n头顶一声巨响，天花板被轰然炸开，沉重的铁板像断头台的刀刃一样骤然落下，将两人之间的道路垂直劈开。", [250] = "……咳咳。原来如此，是这种计谋吗。如果我再往前一步，现在就该被砸成两段了。即使没砸中，铁板也会像现在这样把我们两个分隔开来，给你争取时间。", [260] = "原来你不是在单纯地逃跑，而是想把我引入这个陷阱啊。甚至连我进攻时的习惯性动作，也被你考虑了进去……", [270] = "……是我失言了，莉维雅。你并没有变弱。", [290] = "是你们变强了，塔莎……已经足够强了吧。可以停下来了吧……", [300] = "不可能的。我们的“使命”就是变强，没有上限地变强。\n我们还会变得更强，称霸基洛普斯扇区，称霸麦戈拉云端，称霸更多的地方……", [310] = "这就是我们存在的意义。所以，我们不会停下来的。", [320] = "即使放任同伴自相残杀？即使让整个扇区化为废墟？", [330] = "那又如何？这就是我们的宿命啊。", [340] = "在抵达“最强”的那一瞬间落幕，就是我们的归宿。每一个斗士都憧憬着这样的归宿。", [350] = "我无法理解这种想法，塔莎。", [360] = "……莉维雅，我问你。在屋顶上的时候，你为什么不瞄准我的头？总不能是怕打坏这张相似的脸吧？", [370] = "欸？", [380] = "你有那个机会的。那一枪如果打的是头，我不可能躲得开。", [390] = "那是……", [400] = "还有刚才的陷阱。最后一刻，为什么你要出声提醒我？如果你早一秒钟启动陷阱，现在我也已经被砸死了。你虽然平时很吵闹，但战斗的时候可从不会多嘴。", [410] = "为什么你不杀了我？如果杀了我，基洛普斯扇区的管理员权限就会完全转移到你的身上，你就能给战斗型智能体下命令，控制住眼下的局面了。为什么不这样做？", [420] = "……我确实打算杀了你。为了杀你，我制定了周密的计划。我欺骗了那个叫席摩的狙击手，让他协助我拆毁隔离墙。", [430] = "我还以监管型智能体们的死为代价，吸引你来到这里，一切都是为了夺走基洛普斯扇区的管理权限，实现我的使命。", [440] = "可是我低估了你的战斗力，我没能杀掉你……", [450] = "你在说谎，莉维雅。我知道，我每次都知道。", [460] = "你很熟悉我的习惯，我也很熟悉你的。当你没有下定决心做一件事情的时候，你就会假装把事情搞砸了。", [470] = "莉维雅，你舍不得杀我。", [480] = "就像当年，你舍不得阻止战斗型智能体那样。", [490] = "……！", [500] = "所有人都以为是战斗型智能体的战斗力突然激增，才会导致基洛普斯扇区的平衡失控。只有我自己心里清楚。", [510] = "智能体的作战能力是不可能突然进化的。我们之所以会变成现在这样，都是因为监管型智能体放松了对我们的监管。", [520] = "按照规定，你应该立即处死、重置那些做出过激行为的战斗型智能体。可是某一天开始，你突然做不了这种事了。", [530] = "……别说了，塔莎。", [540] = "我是一个不称职的监管员。基洛普斯扇区变成现在这样，都是我的错。在这里败给你，也是我应得的报应。", [550] = "你想逃避吗？", [560] = "……也罢。我不会像你一样手软的。就用你实现我的梦想吧。", [570] = "塔莎举起了枪。", [590] = "在塔莎的攻击下，堵在路中的铁板很快被清理出了一个口子。微弱而摇晃的火光从铁板后漏出，照亮了莉维雅虚弱的脸。塔莎静静地瞄准了她。", [600] = "——我看见你了。", [610] = "塔莎……", [620] = "还有什么遗言吗？我会帮你记下的。", [630] = "……", [640] = "——咻！", [650] = "……！", [660] = "塔莎！", [670] = "呜……", [680] = "你……你是……", [690] = "……", [700] = "看起来赶上了。", [710] = "席……席摩？", [730] = "……啧……可恶……", [740] = "呼，也不过如此嘛。架子摆得挺大，结果也没变得多强。", [750] = "顺利压制住渡鸦了！不能再继续在这里消耗时间……", [760] = "我们得赶紧脱身，去找席摩！", [770] = "冷静，帕斯卡。我们必须先完全排除渡鸦的威胁。", [780] = "唔……我明白，教授。", [790] = "那就赶紧把这边解决掉吧！", [800] = "看我的！究极——狄拉克——脉冲！", [810] = "狄拉克的攻击对准了渡鸦。然而，就在克罗琦开火的一瞬间，渡鸦的嘴角却扬起一抹诡异的笑容。", [820] = "不要以为……我会吃同一招两次！", [830] = "下一秒，渡鸦站立的位置瞬间被狄拉克的火力所吞没。\n然而，爆炸结束，硝烟散去的那一刻，众人却发现——", [840] = "不见了？？", [850] = "为什么！她整个人都瞬间消失了？", [860] = "不对，克罗琦！上面，上面！", [870] = "！！！", [880] = "渡鸦并没有消失，而是以难以置信的速度一瞬间抬升了高度，飞到了狄拉克的正上方。", [890] = "怎么会……突然变得这么敏捷？", [900] = "我改变主意了，果然还是最开始的策略正确。“擒贼先擒王”。", [910] = "光束汇集于渡鸦身上。之前几近折断的长枪上，流转的紫光再次炫目起来。", [920] = "去死吧。", [921] = "渡鸦", [930] = "她轻轻抬手，长枪便以无法闪避的速度，似一道天雷一般，垂直朝我所在的位置落下——", [940] = "教授？！", [941] = "帕斯卡", [950] = "呃——！", [960] = "最后一刻，我的视野被巨大的阴影覆盖。\n那是庞大的机兵张开双臂的背影。", [970] = "克……", [980] = "轰！！！", [990] = "克罗琦！！！", [1000] = "机兵在长枪落地的前一秒，挡在了我的头顶。\n不等我们反应过来，它便在剧烈的爆炸声中四分五裂。\n机械碎片四处散落，烟雾弥散，空气中是令人窒息的烧焦的气味。", [1010] = "可恶，烟雾太大确认不了情况……", [1020] = "克罗琦，你还在吗？快回应我们！", [1030] = "……", [1040] = "怎么、怎么会……", [1050] = "哈哈哈哈哈……你们都只是被我踩在脚底下的渺小蝼蚁罢了！我是最强的，我将碾碎一切！", [1051] = "玛拉", [1060] = "咳、咳咳……", [1070] = "这个声音是——！", [1080] = "咳咳……呜呜……", [1090] = "呜呜……狄拉克……我的狄拉克……", [1091] = "我们循声回头，克罗琦从一个烧焦的大铁块里打开了舱盖，踉跄着爬了出来。那个铁块隐约能看得出是狄拉克的头颅，碧绿的双眼已经变得黯淡。", [1100] = "克罗琦！你怎么在我们身后？你没事吧！", [1110] = "我没事，咳咳，我被狄拉克的逃逸舱弹射出去了。", [1120] = "呼，太好了，人没事就好……", [1130] = "呜呜……狄拉克……我一定要为狄拉克报仇！！！", [1140] = "生死关头还想着机兵，该说不愧是克罗琦吗……", [1151] = "谢谢你，克罗琦。", [1152] = "下回想个变身台词吧。", [1160] = "小意思啦，教授！狄拉克……虽然没有了，但还是可以再造的！", [1161] = "嗯，还有狄拉克。", [1162] = "“我只是一个路过的机械师”如何？", [1163] = "挺帅的。", [1170] = "毕竟，这可是英雄专属的动作，<size=24>我也早就想做一次了……</size>", [1180] = "居然还活着……虫子的生命力还真是顽强。", [1190] = "看来我得再调高“档位”了……", [1200] = "档位？", [1210] = "果然。我就说你的作战能力不可能突然上升这么多。那道紫色的光，其实是调用算量时发出的吧？", [1220] = "调用算量？你是说，她正在偷偷从什么地方调用算量到自己身上？", [1230] = "哼……敏锐的虫子真是讨厌啊。", [1240] = "大家小心！她又要攻击了！", [1250] = "帕斯卡援护克罗琦，安冬妮娜和我牵制她！", [1260] = "啧，好的！", [1270] = "按平时配合的那样行动！调取算量需要时间，我们还未必会输给她！", [-999] = ""}
return AvgLang_cpt02_e_08_01_ZH_CN

