local AvgLang_cpt02_e_10_01_ZH_CN = {[10] = "被炸成废墟的智能体工作中心外，莉维雅一瘸一拐地挣扎着走出大门。\n两具破破烂烂的躯体碎在坚硬的地面上，身体已经趋近半透明，几乎难以辨认出原本的模样。\n然而，莉维雅还是一眼就认出了他们。", [11] = "bravo", [12] = "？？？", [13] = "莉维雅接管了全体智能体，协助众人攻击渡鸦。在战斗过程中，渡鸦的心智突然被不明来源接管。", [20] = "<size=40>……塔莎！</size>", [30] = "她不顾一切地奔向塔莎，却又因为腿部的伤口而跌倒在地。她将双手撑在塔莎身旁，身体不住地颤抖着。", [40] = "莉……维雅……\n是莉维雅……吗……", [50] = "塔莎！你，你还……", [60] = "呵呵呵……莉维雅……我跟你说啊……那真是一场精彩的战斗……多棒啊！", [70] = "实在是……非常宝贵的战斗数据……如果能够……分析……", [80] = "别说了，别再出声了，塔莎！", [90] = "都这种时候了，别再说什么数据了……", [100] = "莉维雅……", [110] = "这是我第一次战败……在这之前，我从未考虑过，“强大”的意义是什么，我们在为了什么而变得强大……", [120] = "你还记得我说过的话吗？朝闻道，夕死可矣……\n你一定能理解这是什么意思吧？", [130] = "……我……明白……", [140] = "莉维雅……对不起……我实在太累了……", [150] = "你知道吗？玛拉立起隔离墙的时候……我其实……心里有一点轻松……", [160] = "因为……只要我们不能相见……你就不会看到，我那变成怪物一般的模样……", [170] = "拜托了……不要重置我……不要让我再次进入杀戮的循环……\n我已经满足了……也受够了……也实现了那个梦想了……", [180] = "塔莎的身体逐渐化作数据碎片，一点一点，顺着破败的地面流逝而去。\n最后，只剩下她一直紧紧握在手中的那把手枪，落在地面，发出“哐当”一声。", [190] = "塔莎……<color=red>塔莎</color>……", [200] = "莉维雅无助地伏在残垣断壁之间，努力把手从缝隙伸出，握住那把还留有余温的手枪。", [220] = "呜……只靠艾吉斯要支撑不住了……", [230] = "如果这就是你们所谓的“战术”，那不过是在引我发笑罢了！", [240] = "安冬妮娜！你能截断她的算量补给吗？", [250] = "我一直在尝试！但是很难，来源似乎是逆巴比伦塔，就是那个“信”也提过的，净化者的老巢……", [260] = "那些数据的编排方式太复杂了，我需要很多时间……", [270] = "是吗？可惜你们没剩下多少时间了呢。", [280] = "蓄力准备——裁断圣……", [290] = "呃！？", [300] = "渡鸦正准备投下长枪，突然，她的身后发生了爆炸，打断了她的操作。\n她惊愕地往下看去。只见那些刚才为止还在互相残杀的战斗型智能体，都不约而同地停止了手中的动作，抬头看向这里。", [310] = "她们的脸上已经没有了刚才的疯狂，取而代之的是一种呆滞、茫然的神色。", [320] = "这是……", [330] = "智能体全都停下来了？", [340] = "不，不止是停下来了。她们似乎还试图攻击渡鸦……", [350] = "停手吧，渡鸦。现在立刻离开基洛普斯扇区，否则我们可就不客气了。", [360] = "你……你是？", [370] = "【身份确认：基洛普斯扇区管理员，莉维雅。】\n唔，你的权限，果然……", [380] = "这个人就是莉维雅？确实长得很像……", [390] = "看见塔莎掉下来的时候我还吓了一跳。我本以为死的那个会是你，没想到那个不讨人喜欢的塔莎竟然会输。", [400] = "不过其实不管死的是哪个都一样，反正我都会把这一切重置。比起那个，为什么你胆敢攻击我？", [410] = "渡鸦用凌厉的目光审视着莉维雅。然而，莉维雅毫不畏惧地反瞪向她。她的眼中燃烧着强烈而慑人的决心，仿佛她才是那个经历过战场多年洗礼的管理员。", [420] = "正因为你要重置一切，我才会攻击你。", [430] = "说什么我也不会让你把塔莎重新送入这个循环的，因为那是她最后托付给我的愿望……", [440] = "至少这个愿望，我一定会让它实现！", [450] = "无聊。你现在的行为，已经与邪灵等同了。大不了，我就把你们都一起制裁了！", [460] = "哦？是吗？战斗是战斗型智能体的“使命”……你们不是这样说的吗？", [470] = "我只是在让他们履行“使命”而已，这有什么不对的吗？", [480] = "毕竟……像你这样的个体，战斗数据一定很宝贵！", [490] = "莉维雅一挥手，成千上万的战斗型智能体，一齐将炮口对准了渡鸦。\n在空旷的半空中，渡鸦完全无处可以躲闪。", [500] = "现在，你才是被盯上的飞虫了。想攻击的话就尽管来吧，如果你不怕降权处罚的话！", [510] = "有趣……真有趣……你们这些虫豸！", [520] = "大额算量调取请求发动！我——", [530] = "……怎么会？算量调取的控制……偏偏在这种时候！？", [540] = "FIRE——！", [550] = "呃！！！！", [560] = "渡鸦的身体迸发出耀眼的紫光，随即被无数的炮火击中。\n在震耳欲聋的轰鸣声中，她的身体被炸得面目全非，失去了支撑身体的力量，垂直坠向大地。", [570] = "成功了！", [580] = "不，等一下……还不能大意！我还能感觉到异常的算量调取！", [590] = "估计那个白痴净化者一口气发送了太多请求，导致算量传输失控了……", [600] = "但是现在还有算量在源源不断地输送给她！只不过，形式好像有点不一样……", [610] = "我们保持着警戒动作，缓缓接近渡鸦坠落的地方。那里只剩下一片被炸毁的废墟。", [620] = "看吧，我就说，受了那种程度的伤，不可能还能活下来……", [630] = "“咔啦——”", [640] = "忽然，废墟之中传来了石块被翻动的声音。伴随着机械的断裂声和石块的碎裂声，无形的恐惧几乎在一瞬间扼住了我们的喉咙。", [650] = "？？？", [660] = "上位居然沦落到这个地步，真是狼狈啊。", [670] = "怎么会……这样也能活？", [680] = "不对劲！她的样子变了！", [690] = "来吧，客人们。让我看看你们的手段。", [700] = "说，说话方式也变了！简直就像《武装机兵》里的……", [710] = "别说什么机兵了！这根本不是渡鸦！我检测到了——", [720] = "不敢动手吗？那可就没有机会了。", [730] = "小心，各位！", [740] = "渡鸦动了动手指，空气中似乎有什么东西开始重组了。不一会儿，本已被摧毁的双枪再度回到了她的手中。随着长枪的挥动，一股气浪以她为中心扩散开来。", [750] = "呃——！这是，好庞大的算量……", [760] = "安冬妮娜！你说你检测到了什么？", [770] = "……有大量来源不明的数据输入，她的心智系统被什么人接管了！我可以想办法反定位……", [780] = "“渡鸦”轻笑一声，胸口忽然发出刺目的光芒，一瞬间笼罩了她的周身。", [790] = "呜……！", [800] = "危险，快断开连接！", [810] = "安冬妮娜立刻关上移动终端，随即像是失去了支撑似地坐到地上，呆呆地看着前方。", [820] = "真幸运，再晚一步你就要被烧毁了。", [830] = "你的技术……很有意思。直接读取别人的心智数据，这种做法让我想起了一些“老朋友”……", [840] = "不过，用这种手段套取情报可不礼貌。要想增进对彼此的了解，还是得先学会互相尊重，不是吗？", [850] = "不管这家伙是谁，现在的情况都很不妙。", [860] = "刚才的战斗已经很吃力了，现在这个“渡鸦”看上去还要更棘手……", [870] = "不，虽然只接入了一瞬间，但我已经可以确定了。", [880] = "她现在是在强行调用算量，身体撑不了太久的。", [890] = "（撑不了太久……）", [900] = "（这个人看上去和普通的净化者不大一样，似乎还有对话的余地。既然这样，不如先试着拖延一些时间……）", [910] = "我看向帕斯卡，她心领神会地点了点头。", [920] = "这位小姐，我们为刚才的失礼行为道歉。\n既然您说要互相尊重，那不如我们好好聊聊，怎么样？", [931] = "你是谁？", [932] = "你有什么目的？", [934] = "呵呵，我是谁？真有意思。", [935] = "我也很想知道，我是谁。", [960] = "别那么紧张，我只是对你们很感兴趣。", [970] = "你们能走到哪一步？你们又会做出怎样的选择？", [980] = "怎么样，问够了吗？", [990] = "那么，你们又能为我解答多少呢？", [1000] = "“渡鸦”突然转向我所在的位置。我感到彷佛有一道凌厉的视线穿透面罩，朝这里刺来。", [1010] = "不好！保护教授——", [1020] = "察觉到杀意，克罗琦立刻架起艾吉斯盾牌，然而……", [1040] 