local AvgLang_cpt02_e_11_01_ZH_CN = {[10] = "果然……这副身体还是无法承受住呢。", [11] = "bravo", [12] = "系统", [13] = "席摩", [14] = "萨可", [15] = "众人击败了心智被接管的渡鸦，得知了一切的真相。\n在莉维雅的帮助下，众人重置了席摩，并解除了他的抑制程序。在获得管理员权限后，教授为基洛普斯扇区开启了沙盒屏障。", [20] = "“渡鸦”的右腿关节因数据过载而爆炸断裂，手臂关节也开始冒出火花。", [30] = "有效果了！", [40] = "看来要——继续@#￥！@%——", [50] = "她是……卡住了吗？", [60] = "“渡鸦”的后脑冒出一股浓烟，火苗从烧毁的脑壳中窜出。", [70] = "流亡者——", [80] = "我会一直看着你#%*！&——们——", [90] = "在螺旋——的顶端——", [91] = "“渡鸦”？", [100] = "“渡鸦”的身体跪倒在地，冒着浓烟的脑壳发出嘈杂的声音。她的关节脱落，身体散落成了碎片，渐渐化为数据消失了。", [110] = "随着“渡鸦”的死去，隔离墙也彻底失去了算量来源，逐渐黯淡、分解，最终化作点点橙色的星光，散落在高墙两侧的智能体身上。", [120] = "所有的智能体们，都平静地注视着这一切。没有疯狂，没有偏执，没有执念。每个智能体的脸上，都只剩下如水的宁静。", [130] = "基洛普斯扇区的高墙，再也不复存在了。", [150] = "……我很抱歉……", [160] = "……", [170] = "不是你的错，莉维雅。这是席摩自己的选择……", [180] = "莉维雅告诉了我们一切。", [190] = "我们从大楼的废墟里找到了席摩一直挂在头顶的面具。在这之前，我们从未见过他摘下这枚面具。\n第一次翻开面具的背面，我们立刻注意到一个清晰的编号。", [200] = "这是……火神重工的实验体编号……", [210] = "席摩是作为火神重工的原型实验机诞生的。基洛普斯扇区，也是那个实验的一部分。换言之，席摩就是我们的原型机……", [220] = "我们每个智能体的底层代码，都是在收集过席摩于现实世界的战斗数据基础上编写而成的。", [230] = "席摩知道这件事吗？", [240] = "不知道。他来到基洛普斯扇区后才查明了这一点。", [241] = "就如你们所见的，这里的智能体，总是哪里很像他……", [242] = "在这之前，他完全不知道自己的代码正以这种形式被传递着。", [250] = "他本以为离开了战场，自己在战争中犯下的罪孽就能不再累积。", [251] = "可他没有想到，那些数据早已在云端散开枝叶，遍布在每个战斗型智能体的身上。", [260] = "所以他才会那么想要终止基洛普斯扇区的战争吗？不仅是为了拯救你们，也是为了阻止自己的罪孽继续累积……", [270] = "一开始他确实是这样想的，不过，我想在最后一刻，他已经把这些目的都放在次级了吧。", [280] = "他在最后更想要拯救的，是你们。", [290] = "我们……", [300] = "是我欺骗了席摩，导致你们身处险境。他认为只有杀死塔莎，才能平息骚乱，保证你们的安全。", [310] = "……于是他就在和塔莎的战斗中同归于尽了吗……", [320] = "不，那不是同归于尽，帕斯卡。是席摩主动选择了死亡。", [330] = "安冬妮娜？为什么……", [340] = "你们还记得那个时候我说过的话吗？", [350] = "唔……没什么，只是有点在意他刚才的样子……", [360] = "在意？席摩吗？", [370] = "嗯。你们没注意到吗？一段时间不见，他的动作看上去比以前要僵硬不少，就像在忌惮着什么事情一样。", [380] = "这么一说……", [390] = "我总觉得席摩隐瞒了什么，所以刚才，我调查了这里残留的记忆碎片。", [400] = "然后我就发现了……席摩一直瞒着我们的秘密。", [410] = "秘密……？", [420] = "克罗琦，你听说过火神重工代号“角斗士”的“抑制程序”吗？", [430] = "唔……这么一说，好像是听说过这个词，据说是早期为了防止战斗用人形暴走，而附加的保险措施，后来实验效果不理想就取消了。", [440] = "保险措施……在人类看来或许的确如此吧。但对人形来说，那根本就是用来拴奴隶的枷锁罢了。不过角斗士原本也就是奴隶就是了。", [450] = "等一下，难道席摩就是——？", [460] = "你猜得没错，席摩就是那个“抑制程序”的实验品。", [470] = "如果他在短时间内进行过多次数的攻击性行为，就会导致抑制程序过载，对他施加肉体上的痛苦。", [480] = "痛苦达到极限，最终就会启动自爆程序。这样，即使人形失控，也能在危害扩大之前自毁。", [490] = "怎么会……？但席摩从来没有……", [500] = "从来没有告诉我们，对吧？他一直在默默地承受那些痛苦。", [510] = "看……这是席摩在记忆碎片里，留下的自白。", [520] = "过去，人类希望我不要过度杀戮，却又总是驱使着我去进行战斗。当抑制程序过载的时候，我的每一个攻击动作都伴随着巨大的痛苦，却又被命令着，不能停下。", [530] = "最后我成了失败的实验品。买下我的人将我投入战场，当他们希望我在短时间内大量杀敌的时候，他们就会先让我的抑制程序过载，然后再重置我的心智。这样一来，就能够绕过抑制程序的限制了。", [540] = "于是在没有止境的杀戮中，我反复沉沦……直到遇见了教授、帕斯卡，遇见了流亡者们。", [550] = "我们……", [560] = "你们是第一批接纳我的人。只有和你们在一起，我才不会被当做纯粹的杀人机器。在你们身边，我第一次觉得，手中的武器也有了存在的意义。", [570] = "所以，请原谅我的决&*定￥%……也请不要^&担心#$我。", [571] = "没有关系的……因为我们是同伴，因为你们相信我……", [580] = "@#￥！&——*&￥#！！￥——噼啪*&￥#！！￥——", [590] = "……", [600] = "席摩是主动让自己的抑制程序过载，启动自爆程序，继而引发连环爆炸的。", [601] = "为了保护我们，他冲破了束缚自己的枷锁。成了一个获得自由的“角斗士”。", [610] = "……", [620] = "获得了自由，却依旧选择了死亡。不，所以才能选择死亡吗……难怪是甘尼克斯吗……这个笨蛋。", [630] = "席摩……", [640] = "好了，一切准备就绪了。帕斯卡，依靠你存下的数据，我们还能重置席摩。", [650] = "多亏了莉维雅提供的扇区数据，我成功解析了抑制程序的构造。", [651] = "按照这个破解方式，重置后的席摩不会再受到抑制程序的束缚。", [660] = "辛苦你了，安冬妮娜。也谢谢你，莉维雅。", [670] = "没什么。我利用了席摩，这也算是一点点赎罪吧。", [680] = "但我还是得提醒你们……这个席摩，和过去的席摩，是不一样的。", [690] = "嗯，我们明白。人形的经历与记忆是无法保留的。保存下来的数据，只能让人形回到“出厂状态”。", [700] = "……但即使如此，我们也要重置席摩。", [710] = "我明白了。我相信你们一定能带他摆脱基洛普斯扇区的阴影。", [720] = "从今往后，没有了抑制程序，他能够自己选择守护何人，也能自己承担犯下的罪恶。", [730] = "希望……你们能带领着席摩，找到属于他自己的路。", [740] = "嗯，我们一定会陪伴在他身边的，我向你保证。", [751] = "我也向你保证。", [760] = "谢谢你们，教授，帕斯卡，安冬妮娜……", [770] = "那么……", [781] = "开始吧。", [790] = "……嗯。开始吧。", [800] = "开始还原心智备份，执行指令：", [801] = "<color=orange>Rebuild</color>。", [802] = "莉维雅", [820] = "吱——！", [830] = "萨可从帕斯卡的肩头跳到席摩身上，正要像往常一样蹭上去，却突然又往回缩了缩，小心翼翼地用鼻子嗅了嗅席摩的脸颊。", [831] = "【正在进行初始检测……】", [840] = "……吱？", [841] = "【已确认智能体序列：279b79227b35941cf4b3。】", [842] = "【请输入权限码。】", [843] = "Gannicus71。", [844] = "【智能体正在启动……】", [850] = "……", [860] = "席摩，听得到我们说话吗？", [870] = "席摩的眼睛慢慢睁了开来——\n他的眼神和过去的席摩完全不同，澄澈干净，连一丝杂质都不曾有。", [880] = "【正在进行初始检测……】", [890] = "【确认型号。】", [900] = "【角斗士-Gaul。】", [910] = "【确认归属。】", [920] = "【火神重工军事EE部03研究组。】", [930] = "【确认心智系统语言类别。】", [940] = "【SVAROG+。】", [950] = "【确认心智加密等级。】", [960] = "【S。】", [970] = "【确认完毕，正在启动……】", [980] = "【启动完毕。】", [990] = "……", [1000] = "请问……", [1010] = "再度苏醒的席摩，一眼就看到了正在操作系统的莉维雅。", [1020] = "您是我的指挥官吗？", [1030] = "……", [1040] = "莉维雅……", [1050] = "我……", [1060] = "……", [1070] = "不，我不是。", [1080] = "Gaul，你的指挥官是——", [1090] = "她将手指朝向了我。", [1110] = "真的没问题吗？莉维雅。就这样把扇区权限交给我们……", [1120] = "嗯，你们拿着吧。我带着智能体，和上位净化者闹了那么一大场，今后恐怕是没办法再和他们正常相处了。", [1130] = "而且，我信任你们……请你们开启沙盒屏障吧。", [1140] = "等你们回绿洲之后，我也会逐渐把算量调配过去的。请放心。", [1150] = "……嗯。真的非常感谢你，莉维雅。", [1160] = "我才是要感谢你们。我不是一个称职的监管员，自始至终，我没能阻止任何人……", [1161] = "但多亏了你们的帮助，我才得到了弥补的机会。", [1170] = "基洛普斯扇区的智能体，永远只能追求杀戮、进化，再走向灭亡。这就是我们身上磨灭不了的“使命”，我们存在的意义。", [1180] = "但今后，我会努力履行监管型的义务，尽量避免这样的混乱再度发生。", [1190] = "莉维雅……", [1201] = "加油！", [1202] = "如果还需要什么帮助，可以随时来绿洲找我们。", [1210] = "谢谢……谢谢您，教授。", [1220] = "也谢谢你，安冬妮娜，是你帮我改进了我们的战力控制程序。", [1230] = "嗯……", [1241] = "你是怎么做到的，安冬妮娜？", [1242] = "不愧是安娜，轻易就做到了我们做不到的事情！", [1250] = "……不过是一些巧合罢了。", [1260] = "你们还记得刚进入基洛普斯扇区时发生的事情吗？", [1270] = "厉害啊！不愧是安娜，比传说中的还要高效呀。", [1280] = "是安冬妮娜。不过，果然这里的协议也是……", [1290] = "唔……算了。赶快进去吧，不然净化者又要来了。", [1300] = "当时我发现，基洛普斯扇区的各项数据协议，在“归零事件”发生后不久就停止了更新。", [1301] = "利用那些旧的协议，我很快就解析了战力控制程序。", [1310] = "但这也意味着，如果协议及时得到更新的话——如果云端没有和现实世界断连的话……", [1311] = "或许智能体们就不会失控、不会陷入无休止的战争里了。", [1312] = "说到这里，安冬妮娜看向了我，眼神里带上了几分寒意。", [1320] = "先是罗萨姆扇区，然后又是基洛普斯……云端和现实世界的断连，已经让太多的智能体遭受了不必要的痛苦。", [1330] = "可我们甚至至今不知道这些痛苦因何而起。", [1340] = "教授，在人类世界，到底发生了什么？", [1341] = "安冬妮娜", [-999] = ""}
return AvgLang_cpt02_e_11_01_ZH_CN

