local AvgLang_cpt02_h_04_01_ZH_CN = {[10] = "麦戈拉上空，逆巴比伦塔内部。", [11] = "？？？", [12] = "圣餐", [13] = "渡鸦在万圣之殿重生，迎接她的是神秘的上位净化者——圣餐。她为渡鸦提供了指引，但目的似乎不止于此。", [20] = "哼~哼哼哼~哼哼~♪", [30] = "少女坐在漂浮的银盾上，双脚垂在半空，悠悠然地操纵着悬浮的光幕。", [40] = "呼~啊，肚子都忙饿了！", [50] = "唉，新人还是太嫩了，算量储备也不行，居然被自己申请的算量弄坏了。", [60] = "到头来还要刚出差回来的前辈来收烂摊子……", [70] = "少女唉声叹气地说着，按下了最后一个虚拟按键。", [80] = "好，这样就可以了！", [90] = "出来吧，莽撞的小后辈——", [100] = "殿堂内，一块构体发出刺眼的光芒，金色的光线争相从刻着“渡鸦”标识的缝隙中透了出来。", [110] = "整个大厅被圣光笼罩着，远处隐隐响起了钟声。", [130] = "原来上位净化者的重生是这样的呀！", [140] = "真是稀奇，稀奇——不过，光是奇闻也填不饱肚子，还是快点结束工作吧！", [150] = "小渡鸦，你知道这是哪里吗？", [160] = "这里是……收藏着我等<a href=Des:46>圣典</a>的“万圣之殿”……", [170] = "答对了！看来你保留着基本的记忆嘛。", [180] = "这点和中级净化者重生的时候一样，记下来记下来。", [190] = "……你是谁？", [200] = "我吗？我是你的老前辈哦。", [210] = "“圣餐”，你就这样叫我吧！", [220] = "前辈……“圣餐”。", [230] = "圣餐，我上一次的行动失败了吗？", [240] = "嗯哼~就是这样。", [250] = "上位净化者面对<color=purple>“那些东西”</color>战败不稀奇，毕竟我们原本就是为了和它们作战而生。", [260] = "但在管理扇区时战败……我看看记录……", [270] = "……", [280] = "嗯！这在净化者的历史上还是第一次哦！", [290] = "真是耻辱……", [300] = "小事小事，只是丢失了一个扇区而已，总有办法解决的啦。", [310] = "丢失了……一整个扇区？", [320] = "是我做的？", [330] = "是呀。本以为派你去管理扇区能让你稳重一些的，没想到还是这么冲动。", [340] = "我们上位净化者天生就背负着更严峻的使命，可不能再那么莽撞了哦。", [350] = "这……实在是难以忍受的耻辱……", [360] = "我能够弥补它，让我去夺回它！", [370] = "这份心是很好啦。但你想用什么方式去夺回这个扇区呢？", [380] = "我们没有你那次战斗的数据，你能保证自己不会被原样打回来吗？那可真的就是毫无长进了哦？", [390] = "……", [400] = "真稀奇，你平时气急败坏的时候可是会砸东西的，战败对你的影响这么大呀？", [410] = "嘛，算了~来说点更有意义的事吧！", [420] = "不说那个扇区，麦戈拉其他扇区的事务可是堆积如山。边境战线那边也是，很棘手呢……", [430] = "……战线？", [440] = "重复着这个词，渡鸦像是忽然找到了什么方向一般，急切地询问圣餐。", [450] = "对……对，战斗！圣餐，我要去需要战斗的地方！", [460] = "这方面还是没变啊……有点没意思呢。", [470] = "圣餐轻巧地跳下银盾，绕着渡鸦转了一圈。", [480] = "如果战线需要我——", [490] = "别那么心急。你就先在这里守着万圣之殿吧，说不定很快就会有新的任务呢~", [500] = "……是，我不会再让净化者的名誉蒙羞的。", [510] = "圣餐摆了摆手，离开了万圣之殿。", [520] = "迎着殿外纯净的光芒，少女眯起眼睛。", [530] = "名誉蒙羞啊……", [540] = "先是信，再是小渡鸦，那些“流亡者”果然很有趣呢。", [550] = "我也得振作起来了！", [560] = "好，决定了——先去吃点东西吧！", [-999] = ""}
