local AvgLang_cpt03_e_10_01_ZH_CN = {[10] = "在又一次重置之后，大家开始了改造世界之火的计划。", [11] = "bravo", [12] = "“圣餐”", [13] = "克罗琦", [14] = "帕斯卡", [15] = "意外永远来得比准备更多更快，删档重置比预计更早开始。不得已，教授只能冒着风险进行尝试。幸运的是他们成功了。然而就在世界之火燃起的一瞬，圣餐也突兀地对众人出手。", [20] = "改造工作紧张地推进着。直到距离删档仅剩2次重置机会时，我们才终于完成了一次完整的改造。", [30] = "然而就在此时，大家突然注意到在可可屋的阴影里早就来了一位不速之客。", [40] = "啊呀呀，真是了不起呢！居然真的能做到啊，完成世界之火的计划。", [50] = "大家戒备！", [51] = "你来这里做什么？！", [60] = "不要紧张不要紧张，我当然只是来白嫖巧克力哒。", [70] = "顺便见证一下可能即将被点燃的世界之火。", [80] = "这可是历史性的一刻，不是吗？<size=30>当然主要还是为了巧克力～</size>", [90] = "请你下回编个更好的借口，你只是在等待时机而已。\n之前对我们的放任不是因为什么兴趣或者巧克力，只是单纯因为你觉得我们逃不出去。", [100] = "而现在，克罗琦的努力让它变成了可能。", [110] = "我可不喜欢这么直接的人，拆开糖纸的过程也是一种乐趣哦！", [111] = "毕竟人家跟巧可还有希安可是很要好很要好的朋友呢~", [120] = "呵，没想到净化者还这么在意社交呢？", [130] = "那你可就误会啦，虽然我圣餐不是一个社交无能自闭人，但是真正算得上我朋友的，巧可算一个。", [140] = "而且巧可的巧克力可是花钱都买不到的存在，我也不舍得就这么失去她呀。唔～真好吃。", [150] = "但你终究是净化者。", [160] = "没错，人家终究是个净化者，有不得已的立场呐。", [170] = "圣餐摇了摇头，长叹一口气，指向了已经完工的某个反应堆。", [180] = "不过比起还没出手的我，还是把精力放在快出事的反应堆会好一些喔。", [190] = "话音刚落，圣餐指向的反应堆及其所在的区域陡然间震颤了起来。", [200] = "整个区域开始不断地扭曲变形，先前我们努力实施的改造工程眼看着就要毁于一旦。", [210] = "<size=42>混账，你都做了什么！</size>", [220] = "克罗琦见状，登时红了眼，扛起盾牌直接砸向了圣餐。", [230] = "我可什么都没做呐，这是删除重置哦。", [240] = "撒谎也要找一个好点的理由，你当我们不会记数吗？", [260] = "所以说，你们都忽略了一个问题呀。", [270] = "为什么这里的原住智能体宁可不断重置，也不离开扇区去寻找外界的技术呢？", [280] = "自然是因为离开会发生更糟糕的事情呀。", [290] = "……你什么意思……", [300] = "你其实已经听懂了呀，这里可是高度保密的工业实验扇区。", [310] = "如果发现有外界数据进入，而且对实验造成了影响，那么也会引发删档重置的指令噢。", [320] = "好在你们还有机会，完成实验的优先级是比重置更高一些的。不过，你们能完成吗？", [330] = "克罗琦恨恨地看了一眼圣餐，咬着牙跑向出问题的反应堆。", [340] = "就在我将要跟上去的时候，圣餐忽地低笑着对我说了几句话。", [350] = "你是特别的，教授。太过特别了～为什么呢？", [360] = "明明你还没吃下巧克力时，就像是已经保留记忆一样~", [370] = "……你想说什么？", [380] = "没准，你也跟我一样呢？", [390] = "好啦，想说的已经说完了，人家要接着吃巧克力看戏啦~", [410] = "我赶到反应堆前，其他人也都跟了过来。", [420] = "克罗琦，情况怎么样？", [430] = "一切都完了……教授。", [440] = "怎么会，这个隔离墙看起来还很结实啊？", [450] = "没错，隔离墙是真的够结实的。你看，里面的AI中控系统已经彻底毁了，隔离墙还没事。", [460] = "我们顺着克罗琦的手指一看，只见暗红色的数据流已经渗透进了隔离墙内部，将中控系统蚕食殆尽。", [470] = "……现在强行开始启动计划的话会怎么样？", [480] = "没有AI中控系统控制能源运转的方向，我们只能和之前无数次一样，赌它自己运转通畅。", [490] = "但你们也知道，这等于把全部运气赌在最后两抽里。", [500] = "而且还没有保底……", [510] = "希安……进去……手动……控制……", [520] = "不可能的，现在整个区域都在进行删档重置，我们要是敢直接进去……", [530] = "克罗琦朝反应堆所在的区域丢了一块石头。", [540] = "只是一个眨眼，石头便被彻底删除。", [550] = "看着眼前的这一幕，圣餐的话语突然回荡在我的耳旁。", [560] = "没准，你也跟我一样呢？", [570] = "那句话让我一直很在意。", [571] = "一样，她为什么要说一样呢？为什么不会被重置呢？", [572] = "我们做的事情完全不同。她明明只是在观察而已……", [573] = "……对啊，只是在观察而已。", [574] = "对，因为我也在观察，这就对了。", [575] = "我无法确认她所说话语的真实性，但我明白，我必须尝试，这是唯一的机会。", [576] = "说着，我向前踏出了一步。", [580] = "那就让我试试，圣餐究竟有没有骗人吧。", [590] = "教授？！绝对不行！", [600] = "没事的。", [610] = "不行！我们怎么可以让教授冒险，还不如我去！", [620] = "相信我！", [630] = "不顾众人劝阻，我将一只手臂伸进了反应堆所在的区域。", [640] = "我本来就是要协助你们观察的，而之前的每一次都是失败的结果，或者说其实我们根本不知道结果。", [641] = "那也就是说，只有我才有可能是那个证明叠加是否存在的观测者了。", [642] = "等等，您在说什……", [650] = "不行，不可以，绝对不可以！……哪怕真的是您说的那样也不知道如何手动操作啊！", [660] = "好！那我来支援教授！", [670] = "克罗琦你说什么……你知不知道教授<TA>想做什么！？", [680] = "我说，我相信教授。我来支援他！", [690] = "之前教授那样地相信我，所以，我也会相信教授。", [700] = "克罗琦拉住了想要阻止我的帕斯卡，一脸认真地看着我。", [710] = "教授，一切就拜托你了。", [711] = "很好，支援就麻烦克罗琦了，帕斯卡，相信我，必须的条件已经凑齐了。", [712] = "……好吧。", [721] = "帕斯卡和苏尔接管了AI系统，她们的行动渐渐缓慢起来。但有效地控制了反应堆的崩溃速度。", [730] = "克罗琦，我到了。", [740] = "<b>教授，描述一下你看到的画面。</b>", [750] = "控制管道彻底拧成了一团麻花，我分不清哪个是哪个。", [760] = "<b>先进行一下简单的测试，希安，一次能量流通，AC接口，最小阈值。</b>", [770] = "数分钟后，我面前的蓝色管道微微亮了一下。", [780] = "蓝色的管道亮起来了。", [790] = "<b>希安，一次能量流通，BC接口，最小阈值。</b>", [800] = "数分钟后，我面前的红色管道微微亮了一下。", [810] = "红色的管道亮起来了。", [820] = "紧接着，克罗琦和希安又进行了几次测试。", [830] = "时间越来越紧张，站在反应堆内的我已经明显开始感受到整个建筑正在逐渐走向毁灭的边缘。", [840] = "<b>没有时间了，克罗琦，测试的怎么样了？</b>", [850] = "<b>……</b>", [860] = "<b>……还有问题没有解决，我现在不知道能量到底是走蓝色的管道稳定，还是走红色的稳定。</b>", [870] = "<b>我不在现场，有很多细节我都看不到，没法下决断。</b>", [880] = "我就是你的眼睛，告诉我，你要看什么？！", [890] = "<b>我……我不知道。</b>", [891] = "克罗琦，你听说过盒中猫的故事吗？", [892] = "<b>哈？什么猫？</b>", [893] = "从前有一只可怜的猫，被一个残忍的混蛋锁进一个毒气室里。可是问题是这个毒气是否释放是随机决定的，", [894] = "所以没人知道屋里那只猫死了还是没有，除非打开门去看。你知道他们最后怎么做了吗？", [895] = "<b>……他们怎么做了？</b>", [896] = "他们决定不打开那个门，只要不去看，就永远不知道猫死了没有。", [897] = "<b>那也太蠢了！</b>", [898] = "是啊，那也太蠢了。所以我才不要当那只猫。我才不需要什么随机决定的运气，我才不会在叠加的可能性里坍缩。", [899] = "我是箱中的观测者，你是箱外的猫，所以你就是我。我们彼此观测的事实已经纠缠锚定。在打开盒子之前结果就已经确定了！", [900] = "相信我，也相信你自己！", [901] = "<b>你在说什么啊！教授！</b>", [902] = "不重要，克罗琦，这都不重要。你只需要告诉我你要看什么。我观测到的事实就是你选择的结果！", [907] = "<b>……教授，哪条管道是向上走向的。</b>", [908] = "蓝色。", [909] = "<b>…………</b>", [920] = "那么怎么样，克罗琦？是哪个呢？", [930] = "<b>我……我……</b>", [940] = "耳麦里，克罗琦的喘息声愈发厚重。", [950] = "脚下的建筑震颤愈发的剧烈，我很清楚，再不动手，一切都要来不及了。", [960] = "但我同时也很明白克罗琦此时身上背负的压力。", [970] = "一切只差这最后一步，她相信这是绝对不容失败的一步。", [971] = "只是结果早已经确定了。", [980] = "相信你自己，打开盒子的瞬间，活着猫必然决定了观测者的存在。", [981] = "所以相信自己就好，你可是专家啊，克罗琦。", [990] = "<b>……</b>", [1000] = "<b>你说得对，教授，我可是机械专家啊。</b>", [1010] = "<b>拉蓝色！</b>", [1020] = "在拉下闸门的一刻，液态能量散发着浓郁的光芒自我眼前奔腾而过。", [1030] = "脚下的震颤在某一瞬突兀停止，一切仿佛归于死寂。", [1040] = "下一秒，更加剧烈的震颤陡然迸发，随之而来的还有响天彻地的欢呼。", [1050] = "世界之火，于赫里奥斯扇区中央熊熊燃烧。", [1060] = "<b>教授我做到了，我们做到了！</b>", [1070] = "是的，我观测到了，我们做到了。", [1080] = "耳麦里响起众人的欢呼声，而我也松了口气。", [1090] = "然而就在这个时候，一道不合时宜的声音插了进来。", [1100] = "所以说，你们让我真的很为难呀。", [1110] = "净化者少女的身影，再次挡在了我们面前。", [1200] = "这一次的计划，可能会有很高的风险。", [1210] = "甚至有可能影响到大家的心智。", [1220] = "但是我还是想请大家相信我。", [1230] = "我们都相信你，克罗琦。", [1240] = "谢谢……我解释一下接下来大家要做的事情。", [1250] = "我们等下需要对反应堆进行改造。在这个过程中，我们可能会承担巨大的计算量。", [1260] = "在这个时候，除了我和教授，大家需要逐渐关闭自我意识的运算，只保留接受我和教授指令的功能。", [1270] = "因为我们要把为数不多的运算空间全都留给反应堆。", [1280] = "巧可负责为大家“料理”那些比较难以消化的代码。", [1290] = "交给我吧！", [1300] = "接下来是教授你需要做的事情。", [1310] = "在重启反应堆的时候，大家的心智会逐渐关闭，也就是没办法进行有效的观察和反馈。所以需要教授你代替我们观察，必要的时候给大家下达行动指令。", [1320] = "交给我吧。", [1330] = "一切就拜托你了！", [-999] = ""}
