local AvgLang_cpt04_e_01_01_ZH_CN = {[10] = "> 无法控制地. . . 想要更多的算量. . .", [11] = "bravo", [12] = "系统", [13] = "绿洲收到了来自计算机扇区恩格玛的求救信息，那里爆发了“算量黑洞”，若是放任不管，可能会威胁到整个云端。教授和帕斯卡两人前去勘察，发现扇区内的原住智能体都已经成为了空壳。", [20] = "> 想要. . . 变得更加强大. . .", [30] = "> 吞噬.  .  . 吞噬.  .  .", [40] = "> 一定要成为那个.  .  . 完美的孩子.  .  .", [60] = "<b>>> CHAPTER 4 //      残   影 . . .</b>", [70] = "绿洲内，总控制室。", [80] = "教授，工作辛苦了。这是给您的咖啡。", [91] = "谢谢你，帕斯卡。", [92] = "啊，咖啡吗？还是不用啦……", [100] = "不用跟我客气的，教授。咖啡的口味需要再做调整吗？", [110] = "现在这样就好。", [120] = "就算不需要咖啡，您连轴转了这么久，也该休息一下了。", [130] = "我知道您想早点完成绿洲的重建，尽快推进搜寻其他人形的工作。", [140] = "但要是因为工作过度倒下了，可就得不偿失啦。", [151] = "你也要注意身体呀，帕斯卡。", [152] = "我对你说这句话还差不多。", [153] = "帕斯卡好像老妈子啊……", [160] = "诶、诶？老妈子？", [170] = "我听苏尔说你已经连续工作30小时了。", [180] = "就算你的心智足够稳定，不用担心过载导致宕机，也不能这样勉强自己。", [190] = "这、这是另一回事。毕竟我们好不容易有了三个扇区的算量，需要尽快安排才行……", [200] = "总之，您也知道，现在绿洲的各项设施都已经顺利重建并运作了，勘察工作也在稳步推进。", [210] = "这样看来，您也是时候松一口气，给自己放个假啦。", [230] = "是吗？看来我要让你们的休假美梦破碎了。", [240] = "安冬妮娜？我记得这个时候你应该在核对检索终端的数据……发生什么事了吗？", [250] = "检索终端检测到了来自其他扇区的求救信息。", [260] = "虽然信号很弱，而且信息本身残缺不全，甚至加密过……但我还是找到了其中的关键信息。", [270] = "真可靠呀，安冬妮娜不愧是42Lab的高级网络安全工程师！", [280] = "咳，我也不是想被夸赞啦……总之，相关数据和信息已经发送到控制中枢这边了。", [290] = "我看看……信息中的这个坐标……是存放量子数据库的<a href=Des:54>恩格玛扇区</a>？", [300] = "我记得在之前的检索记录中，我们没有发现其中存在人形的信号。", [310] = "没错，现在也还是没有。这封求救信息是无差别发送的，我只是恰好拦住了一条。", [320] = "为了弄清楚究竟发生了什么，我追溯回源头，探了探恩格玛扇区的情况。", [330] = "这个扇区内部如同存在着一个“算量黑洞”一般，不断地吸收外界的算量……", [340] = "在此之前我们还从未遇到过这样的情况。", [350] = "吸收算量？！会对绿洲产生影响吗？", [360] = "暂时还没有，应该是扇区内的智能体控制住了影响范围。", [370] = "这封求救信息的署名为“诺依曼”，我推测他可能就是扇区内的原住智能体。", [380] = "但扇区内的情况并不稳定。一旦情况脱离控制，可能数分钟后就会波及到绿洲。", [390] = "……看来这也是恩格玛扇区的智能体向外发送求救信号的原因。", [400] = "我们该怎么做，教授？", [411] = "加固防御系统，削弱对绿洲的影响。", [412] = "前往恩格玛扇区，关停“算量黑洞”。", [420] = "嗯，无论如何我们都要做好最坏的准备。我马上联系克罗琦加固沙盒屏障。", [430] = "我也会持续提供那个扇区的检索数据，确保加固工作更有方向性。", [440] = "但检索终端收集的数据毕竟是有限的。我们缺少扇区内部的数据。", [450] = "你的意思是……需要派遣人员去扇区实地勘察？\t", [460] = "从根源解决问题吗……真有教授的风格。", [470] = "我们这边也需要更有针对性的数据，以便在恩格玛扇区脱离控制的情况下作出应对。", [480] = "要是能派遣人员实地勘察并传回数据，就再好不过了。", [490] = "但以现有的数据来看，恩格玛扇区内部算量波动很可能异常剧烈，会对心智造成影响。", [500] = "如果心智不够坚固，前往勘察的人形很容易因为运算速度和战斗力下降而陷入险境。", [510] = "如果要挑选一个前去勘察的人……", [520] = "我知道你想说什么，帕斯卡。", [530] = "你的心智是人形中最坚固的没错，可你并非战斗人员，独自前往太危险了。", [540] = "但如果因为人数太多成为净化者的目标，就得不偿失了。", [550] = "我和你一起去，有什么事也能彼此照应。", [560] = "绿洲需要一个能够分析勘察数据，指挥防御工作的人。现在这种情况下，绿洲不能没有你，安冬妮娜。", [570] = "……", [580] = "安冬妮娜一言不发地看向我，用眼神示意我帮忙劝说。", [591] = "和帕斯卡一同前往恩格玛扇区。", [592] = "让帕斯卡独自前往恩格玛扇区。", [600] = "我和你一起去，帕斯卡。", [610] = "帕斯卡和安冬妮娜一时之间都愣住了。", [620] = "诶？！但、但是……", [630] = "那个……虽然您作为人类，心智超越所有人形，不会受到算量波动的影响，但毕竟没有作战能力……", [640] = "……帕斯卡，你是在说教授太弱了吗？", [650] = "我当然不是这个意思！教授对于局势的判断和指挥能力绝对是顶尖的，只是……", [660] = "那就行了。既然你认可教授的能力，也该相信他能帮助你在危机关头做出最佳的判断。", [670] = "至少我是信任教授的。", [680] = "这不是信任问题！我只是担心会让教授陷入危险……", [690] = "我们也同样担心你会陷入危险。", [700] = "多一个人，多一份保障。教授也是这么想的，对吧？", [710] = "我相信帕斯卡的能力。", [720] = "我不能认同。", [730] = "我们并不清楚算量黑洞的情况，让我和帕斯卡同行是最好的方案。", [740] = "那么绿洲的检索系统和对外侦查由谁主持？", [750] = "……", [760] = "克罗琦在全力加固沙盒屏障，席摩还在重新熟悉绿洲。", [770] = "苏尔能提供的只有武力上的支援，希安也一样。", [780] = "安冬妮娜，这里需要你。", [790] = "但你是绿洲的处理中枢，如果你遇到危险，我们谁也没法帮教授把绿洲维持得像现在一样好。", [800] = "我不会轻易地……", [810] = "不要争论了，我跟着去。", [820] = "诶？！", [821] = "虽然您作为人类，心智超越所有人形，不会受到算量波动的影响。", [822] = "但毕竟没有作战能力……", [830] = "……如果是这个方案，我同意。", [840] = "我见识过教授在战场上的魄力，优秀的领导者不会只依赖作战能力。", [850] = "安冬妮娜边说着，边悄悄腾出手来操作了几下终端，我的界面上立刻弹出了消息。", [860] = "我会一直在这里等着你们的消息。", [861] = "安冬妮娜", [870] = "遇到任何危险立刻联络，ok？ ", [881] = "嗯，交给我吧！", [882] = "（点头）", [890] = "所以说，既然教授都这么说了，帕斯卡你就别坚持了。", [900] = "时间紧迫，我们就别在争论上浪费时间了。快收拾好装备出发吧。", [910] = "唔，可是……", [920] = "对了！鉴于之前外派勘察的人员总是出现各种各样的问题，我特地改装了移动终端，不仅加强了通讯信号……", [930] = "就算……", [940] = "没错！现在只要到了定期联络的时间，通讯就会自动接通，实时传达勘察者那边的画面和声音。", [950] = "我是说……", [960] = "当然！如果遇到不方便接入通讯的情况，也可以手动设置定期联络是否自动接通，非常人性化……", [970] = "安冬妮娜！", [980] = "……总之，无论如何我都不会让你一个人去的。", [990] = "我是想说，谢谢你担心我。我们会平安回来的。", [1000] = "……咳，我只是想提升行动的成功率罢了。", [1010] = "不过你既然承诺了会和教授一起平安回来，就要说到做到。", [1020] = "我会的。 ", [1030] = "那么，之后就要麻烦您了。准备出发吧，教授。", [1050] = "数十分钟后。\n恩格玛扇区内，我和帕斯卡谨慎地向着求救信息内所标示的坐标点进发。", [1060] = "联系上安冬妮娜了吗？", [1070] = "信号很微弱。扇区内部的算量波动过于剧烈，通讯也同样受到了影响。", [1080] = "所幸安冬妮娜此前加强过通讯信号，我可以分批传送收集的数据和简单的信息。", [1090] = "但……到底为什么会变成这样……", [1100] = "帕斯卡看向扇区内部，映入眼帘的景象远比想象中更加诡异。", [1110] = "……我们已经进入扇区十多分钟了，还没有遇到过一个智能体。", [1120] = "不……准确地说，是“活着的”智能体。", [1130] = "庞大的服务器早已停止运作，冰冷的器械旁，随处可见已经变成空壳的智能体。", [1140] = "能判断这些智能体的状态吗？", [1150] = "嗯，我检查过。无论是原住智能体，亦或是净化者，体内的算量都接近干涸。", [1160] = "由于并非真正地“死亡”，只是没有足够的算量驱动心智运转，所以也无法被重置。", [1170] = "那位发送信息的“诺依曼”也不知道在哪里……", [1181] = "真让人不舒服。", [1182] = "从另一方面来说，我们不用担心净化者的攻击了。", [1190] = "……我也这样认为。", [1200] = "而且扇区内部的算量波动非常剧烈，我体内的算量像是被拉扯一样……", [1210] = "你还好吗？", [1220] = "暂时还没问题，行动和战斗都没有受到影响。我得感谢帕斯卡博士和……为我安装了足够坚固的心智。", [1230] = "只不过，一些心智比较简单的原住智能体和净化者，或许就没有这么好运了。", [1240] = "即便能活下来，行动力和战力也会大打折扣。", [1250] = "是的。扇区内部的算量波动非常剧烈，连我都能感受到体内的算量像是被拉扯一样。", [1260] = "那些心智比较简单的下位净化者，即便没有丧失行动能力，战力也会大打折扣。", [1270] = "你的情况还好吗，帕斯卡？", [1280] = "暂时还没问题。我得感谢帕斯卡博士和……为我安装了足够坚固的心智。", [1290] = "总之，我先按照计划将收集到的数据发送给安冬妮娜……", [1300] = "嘘！", [1310] = "我将帕斯卡拽到角落。寂静的道路上，几个下位净化者正成队走过。", [1320] = "怎么会……照理来说，下位净化者的行动不该像平常一样高效。", [1330] = "谢谢您及时提醒我，要是被它们发现就危险了……", [1340] = "不，帕斯卡，你的说法错了。", [1350] = "不是“被它们发现”，而是“我们发现了它们”。", [1360] = "您是说……", [1370] = "帕斯卡和我交换了一个眼神。", [1380] = "让我们来看看这个送上门的情报吧。", [1390] = "不过，在这之前……", [1400] = "【信息模块初始化完成，正在尝试传输……】", [1410] = "【……传输完成。】", [-999] = ""}
