local AvgLang_cpt04_e_11_01_ZH_CN = {[10] = "<size=40>呜</size>……", [11] = "bravo", [12] = "帕斯卡", [13] = "？？？", [14] = "信使终于被击败，“算量黑洞”也不复存在。为了与埃妮阿克重逢，诺依曼开始了漫长的研究之路。", [20] = "呵呵……看来我还是太小看你们了呢……", [30] = "信使的弓箭早已破烂不堪，身上也遍布战斗的痕迹。即便如此，她依旧搭起箭矢试图瞄准我们。", [40] = "最后一击——", [50] = "信使被克罗琦正面牵制，没能来得及抵挡住苏尔的突袭，终于应声倒地。\n她仍然狼狈地挣扎着想从地面上爬起来。但此时，她已完全失去了早先那股凌人的盛气。", [60] = "呵，我居然也会到这一步……", [70] = "……动手吧。", [80] = "——等等！", [90] = "听到帕斯卡的阻止，原本把刀架在信使脖颈前的苏尔停下了动作。", [100] = "……诶？", [110] = "怎么了……连解决一个敌人这样的事也做不到吗？", [120] = "……不，我们不是你想象中那样的邪灵。", [130] = "我们只是立场和你们不同而已。", [140] = "……别自以为是地说这些漂亮话。", [150] = "信使说着，终于勉强支撑着站了起来。", [160] = "喂，你现在连站都站不稳了，真的还要和我们打吗？", [171] = "我们不会趁人之危的。", [172] = "回去吧，信使。", [180] = "你们……是小看我么？", [190] = "不，我们尊重你。你所做的一切也都是为了恩格玛。", [200] = "我想，圣餐也不希望你被我们处决的。", [210] = "圣餐……那家伙……", [220] = "原来是这个意思啊……", [230] = "一直为你们说话，认为你们不同于其他邪灵……", [240] = "她看上去已经完全被你们说服了。", [250] = "不过我……还是要把话说清楚。", [260] = "我有自己的原则和信仰。下一次交手，我也不会因此而对你们网开一面的。", [270] = "嗯，我们当然知道。", [280] = "……再见了。", [290] = "信使转过身，拖着疲惫不堪的身躯离开了我们的视线。\n回头望去，算量黑洞已然消失，算量渐渐回到了原住智能体和建筑之内，庞大的计算机也逐渐运转起来。", [310] = "真的就让她这样走了啊……净化者可是追着我们的屁股打了这么久诶！", [321] = "我们本质没有区别，只是立场不同，没必要赶尽杀绝。", [322] = "净化者死后也会重置数据并重生，放走她对我们来说或许会有好处。", [330] = "嗯，要么就是永无止尽的战斗，要么就是彼此互相让步和解……", [340] = "净化者虽然有算量和权限，但本质上还是和我们一样被困在这些扇区里。", [350] = "这么说也有道理。教授的想法还真是长远啊……", [360] = "唔，不过幸好恩格玛没事了！", [370] = "不过，其实还有一个更为关键的原因吧？", [380] = "我向帕斯卡眨了眨眼，而帕斯卡长长地出了一口气。", [390] = "……没想到这都被您看穿了呀。", [400] = "确实，因为是暂时调用恩格玛的算量，所以我没办法坚持更久。而信使并没有完全陷入绝境。", [410] = "当时如果苏尔真的动手，可能就会被信使的箭矢刺穿脖颈了。", [420] = "呜哇，原来这么可怕吗……", [430] = "而且随着恩格玛的通讯恢复正常，信使便可以向逆巴比伦塔申请调用算量。这样下去我们迟早会陷入劣势，不如尽快结束战斗。", [440] = "你想了很多嘛，帕斯卡。", [450] = "在教授身边这么久，也该耳濡目染一些啦。", [460] = "不过，这样一来，也总算可以松一口气——", [470] = "我躺倒在地上，恩格玛扇区的天空无比澄澈明亮。", [480] = "教、教授？您没事吧？是不是受伤了……", [491] = "把帕斯卡也拽倒。", [492] = "让帕斯卡扶我起来。", [500] = "——呜哇！", [510] = "帕斯卡本想凑近确认我的情况，却冷不防被我拽倒，躺在了我的旁边。", [520] = "教授！吓我一跳！", [530] = "还是之前在绿洲商量的那个话题——你也该好好松一口气，休息一下啦。", [540] = "真是的……", [550] = "帕斯卡本来有些气鼓鼓的，但抱怨的话还没出口，却忍不住笑了出来。", [560] = "帕斯卡本想将我扶起来，却