local AvgLang_cpt04_h_06_01_ZH_CN = {[10] = "> 警告.  .  .\n> 在未浏览<color=#ffa500ff>当前扇区全部主线剧情</color>文件的情况下，强制读取该文件.  .  .\n> 可能会造成意识紊乱，影响剧情体验.  .  .", [11] = "净化者", [20] = "逆巴比伦塔内……", [30] = "咳……唔……", [40] = "塔在逆巴比伦塔的石阶上，伽芙丽尔几乎快失去了重心。\n她的身体微倾，尽力用着身体内留存不多的算量让自己保持体面。", [50] = "呵，没想到就连这种事……也要费尽力气……", [60] = "是……是信使大人！", [70] = "信使大人您还好吗……这是发生了什么？", [80] = "让一让。", [90] = "那张平日里始终亲切温和的脸上竟然流露出一丝冷峻。", [100] = "今天负责圣沐修复点工作的是爱……我这就去让她做准备……", [110] = "不，愈疗的事不着急。", [120] = "诶？真的没关系吗……您的伤口很深……", [130] = "算量也……可以感知到地一直在流失着……", [140] = "和这点伤相比，还有更加重要的事。", [150] = "你去，到顶层圣殿去找晨星大人。", [160] = "啊……严重到要晨星大人出面了么……", [170] = "还有，给我把贝欧尼斯那家伙找出来！", [180] = "贝……不不不，圣、圣餐大人么？", [190] = "快去啊！", [200] = "……是！", [210] = "我倒是想听听，她会怎么给自己找借口。", [220] = "黑屏转场", [230] = "圣餐大人，请您快一点！信使大人很着急！", [240] = "来了啦，来了啦！别催了！", [250] = "真是的，信使姐姐怎么那么……", [260] = "踏入圣殿的贝欧尼斯话音未落，便看到浑身是伤的伽芙丽尔正紧盯着她。而伽芙丽尔身后的圣座上，此时晨星的光辉也熠熠闪耀着。", [270] = "哦呀……看来信使姐姐在恩格玛也经历了很大的波折呢。", [280] = "……你看上去倒是很得意呢，贝欧尼斯？", [290] = "哇，姐姐看上去好凶哦。", [300] = "放纵入侵者闯入扇区，刻意败在他们的手中……", [310] = "而后在明知自己背负着使命的情况下，对蓄意破坏扇区的行径放任不管……", [320] = "甚至阻碍我对邪灵执行净化……", [330] = "对此，你没有什么要解释的么？", [340] = "在这里，当着晨星大人的面……", [350] = "给我解释一下，你到底怀着什么目的？！", [360] = "伽芙丽尔咬着牙将声调提高了几分，身上的伤口也因为愤怒而向外溢出着算量数据。", [370] = "干、干什么呀！", [380] = "信使，收敛一些。", [390] = "……是。抱歉，我不该被情绪主导。", [400] = "虽说一方之词并不足以定罪，但圣餐，你有什么想辩驳的么？", [410] = "哎，既然晨星大人也这么说了，那我也想问问信使姐姐呢……", [420] = "我们的任务和使命，难道不是彻底净化和消除邪灵吗？", [430] = "没错。你想说什么？", [440] = "但你最后，不是也选择了离开，没有和那些流亡者顽抗到底吗？", [450] = "要说起来，恩格玛的扇区权限丢失，信使姐姐也是要负责任的啊！", [460] = "……你！", [470] = "是吧？嘿嘿，我可是都看到了哟！", [480] = "信使姐姐，打心底里是不是也觉得那些流亡者和一般的邪灵不太一样？", [490] = "……", [500] = "我只是……", [510] = "嗯？是因为什么呢？", [520] = "反正我呀，我觉得他们很特别哦。", [530] = "晨星大人！", [540] = "没事，信使。让她说完。", [550] = "……是。", [560] = "嘿嘿，经历了那么多个扇区，难道信使姐姐没有这种感觉吗？", [570] = "那些流亡者夺走权限之后，并不像普通的邪灵一样简单地“吞噬”算量。", [580] = "相反，在他们的庇护下，那些扇区运作得很好。", [590] = "可你别忘了，基洛普斯的数据整整被删除了一大半！", [600] = "但我听说那是基洛普斯的智能体自愿的呢。", [610] = "可这些扇区失去了净化者的保护，会很容易被其他邪灵入侵，这点你怎么解释？", [620] = "那就看他们的表现了咯。", [630] = "你——这是对我们天职的亵渎！", [640] = "可我就是想知道他们能做到什么地步呀？", [650] = "我想知道，他们到底是谁。", [660] = "但你不能因为你那可笑的好奇心，就将整个麦戈拉置于危险的境地！", [670] = "哎呀，没有那么夸张吧……", [680] = "都在啊，那就方便了。", [681] = "？？？", [690] = "……昏星？", [700] = "我是来报告一件事。", [710] = "说。", [720] = "万圣之堂，丢失了一份中位净化者的圣典。", [730] = "圣典……失去圣典的话，如果遭遇死亡，根本没办法在逆巴比伦塔再重生了……", [740] = "你没有找到重点，信使。", [750] = "中位的圣典只有上位有资格取出。带走圣典，可是会让那个净化者的算量大幅提升。", [760] = "这也是脱离净化者使命约束最好的办法。", [770] = "脱离……使命约束……！", [780] = "负责巡查的中位说，那天唯一见到的出入那里的上位，只有圣餐一人。", [790] = "……", [800] = "贝欧尼斯……这该不会也是你……", [810] = "……", [820] = "圣餐，你是否真的这么做了呢？", [830] = "面对晨星的质问，贝欧尼斯说不出任何话语，只是沉默着看着他。", [840] = "看来，你承认了呢。", [850] = "我来调查事情的原委。", [860] = "圣典丢失的事让我来吧。", [870] = "……不必了。", [880] = "让我来。", [890] = "……", [900] = "我不明白，早在基洛普斯丢失的时候，我就说过要去调查那群邪灵。", [910] = "但现在他们获得的算量，连信使要对付他们也变得很难了。", [920] = "恕我直言，这也有你的责任。晨星。", [930] = "喂，昏、昏星大人……", [940] = "总之我必须介入此事。", [950] = "……", [960] = "那圣典丢失的事，就由你去调查吧。", [970] = "呵呵，我早就告诉过你要盯紧圣餐。", [980] = "在这种地方心慈手软只不过是给自己留下后患而已。", [990] = "渡鸦就算了，现在就连信使也……上位能堕落至此，真是可笑。", [1000] = "收敛一下你的脾气。", [1010] = "……无聊。走了。", [1020] = "……真是的……越来越乱了。", [1030] = "信使，你去愈疗修复吧，再拖下去会出现心智故障的。", [1040] = "……是。那贝欧尼斯的事……？", [1050] = "我来处理。", [1060] = "事到如今，你确实做得很过分了，圣餐。", [1070] = "唔……", [1080] = "坐拥上位的职阶，就要肩负得起上位的责任。很可惜，在你那里这一切都像儿戏一样。", [1090] = "喂，晨星大人，认真的吗？", [1100] = "我现在可是很严肃地在和你对话。", [1110] = "……不行，我无法接受！", [1120] = "由不得你接不接受。到此为止了，圣餐。", [1130] = "你要为你做的事付出代价。", [1140] = "这应该是令人满意的结果了。至于流亡者，伽芙丽尔心想，如果昏星和自己一起去处理，应该不会有太大的问题才是。", [1150] = "也许是因为算量丢失太多，伽芙丽尔的眼前感到一阵眩晕。她再也无法支撑住疲惫的身躯了。", [1160] = "但在失去意识前的最后关头，她却看到了贝欧尼斯脸上、令人无比困惑与担忧的笑容……", [1170] = "<b>>> CHAPTER 4 //     E N D . . .</b>", [1180] = "> 记忆数据文件编号：R******\n> 数据来源：净化者，伽芙丽尔.  .  ."}
