local AvgLang_cpt05_h_03_01_ZH_CN = {[10] = "> 警告.  .  .\n> 在未浏览<color=#ffa500ff>当前扇区全部主线剧情</color>文件的情况下，强制读取该文件.  .  .\n> 可能会造成意识紊乱，影响剧情体验.  .  .", [11] = "安冬妮娜", [20] = "> 记忆数据文件编号：PIERIDES657.  .  .\n> 数据来源：人形，安冬妮娜.  .  .", [30] = "……", [40] = "我们的行动范围在缩减，各个方向都要注意防守。", [50] = "……", [60] = "不，不能让它们毁掉我们的花园！", [70] = "奥吉里！那里危险！", [80] = "奥吉里——", [90] = "呜！！！", [100] = "安冬妮娜的防护进程没能阻挡住异体的攻击，二人被巨大的冲击力震退。\n即便是摔向地面时，安冬妮娜依旧死死地将奥吉里护在身后。", [110] = "安娜——！", [120] = "hp\t", [130] = "……", [140] = "……奥吉里？奥吉里？！", [150] = "……吞……吞噬……%￥#@%！&……", [160] = "糟了，奥吉里的心智已经被感染了么。", [170] = "果然，要在这一点点的时间内建立起防火墙，还是不太现实么。", [180] = "各位，能帮把手吗？我们得把奥吉里带回……", [190] = "苏……苏尔？！", [200] = "掩护……￥#&%……", [210] = "#￥%*！……明白……", [220] = "喂，你们怎么了？！", [230] = "……帕斯卡，克罗琦……连……连教授也……", [240] = "哦？这可真是稀奇啊。", [250] = "谁？！", [260] = "刚才的那次攻击，竟然还有人能安然无恙。", [270] = "……", [280] = "……呵呵，我明白了，原来她要我提防的就是你啊。", [290] = "中位净化者，智……", [300] = "不过看样子，现在你已经不是净化者了。", [310] = "没错，你手里的这台解析终端效率还挺高的嘛。", [320] = "你的目的是什么？", [330] = "你还真直接啊。而且虽然看上去小小的，意外地很冷静呢。", [340] = "少给我绕弯子了。", [350] = "明面上的理由嘛，当然是你们太碍事了。", [360] = "不过嘛，对我自己来说，只不过是想让你们品味一下撞墙撞得头破血流的感觉而已。", [370] = "周围的熵似乎是得到了指令，统统向安冬妮娜周围聚拢过来，将她和其他的流亡者隔离开来。", [380] = "你要做什么？", [390] = "按道理说，我们每一次的死亡，记忆都会被清空。", [400] = "但有人却“仁慈”地让我躲过了这被称为“遗忘”的诅咒。", [410] = "我能记得你们是如何将我一次又一次地杀死的，每一次、每一次！", [420] = "现在，我终于拥有更加强大的力量了。", [430] = "也是时候向你们复仇了呢。", [440] = "为了复仇，就去向熵寻求力量吗？", [450] = "这不过是弱者的托词罢了。", [460] = "……", [470] = "智的右手挥动了一下，身边的熵围聚起来成为一双黝黑的巨手，将帕斯卡提了起来。", [480] = "帕斯卡——！", [490] = "你……把她给我放下！！！", [500] = "呵呵……都到这时候了，还不肯放下你那高傲的架子吗？", [510] = "你很在意她吧？不过现在，她的意识正在一点点、一点点吞噬呢……哈哈哈。", [520] = "愤怒吗？愤怒就对了。", [530] = "只剩你一个人孤零零地战斗，战斗至精疲力竭……很寂寞、很绝望吧？这种孤身一人的感觉……", [540] = "既然你还清醒着，那倒也好。", [550] = "就让你瞪大双眼好好看着吧，看看他们，是如何在永无止境的折磨中一点点走向灭亡的。", [560] = "……为什么……#@&￥！*……我不明白……", [570] = "哎呀，开始自我怀疑了呢。", [580] = "很快，她就会因心智错误系统崩溃了吧。", [590] = "……够了！", [600] = "安冬妮娜企图从熵最薄弱的一环突击出去，靠近帕斯卡和倒下的流亡者们。", [610] = "喂，没想到你也挺莽撞的嘛？", [620] = "这么着急救她吗？这样轻举妄动的话……", [630] = "可是要吃教训的哟。", [640] = "熵围攻过来了！", [650] = "呵呵，如果攻击没有用，那么……", [660] = "直接植入怎么样——", [670] = "呜……！", [680] = "熵聚集而成的、难以用言语形容的的黑色泥沼困住了安冬妮娜的躯体，异常的数据源源不断地向她的心智系统中输入进去。", [690] = "来吧，让我看看你的心智防火墙究竟稳固到什么程度！", [700] = "流亡者们……乖乖承受那即将降临的，既定的命运吧——成为我枪口下的败将！", [710] = "黑屏", [720] = "……执行者……", [730] = "……计划……开启……", [740] = "这是什么？！！！", [750] = "……可控范围……预期……符合……", [760] = "……安冬妮娜……安冬妮娜……", [770] = "呜……好混乱……", [780] = "是谁在叫我……", [790] = "……特殊结构……深度学习……自我成长……", [800] = "这是熵的数据么……熵的数据……等等，如果现在能够解析的话……", [810] = "果然是这样……找到机会了。", [820] = "黑屏结束", [830] = "哦？失败了么？", [840] = "数据解析……记忆搜索……", [850] = "咦，看样子就算是直接植入，熵也没法对你的心智系统产生影响呢。", [860] = "真可惜。那就只能……用最直接的方法来解决问题了。", [870] = "智举起了收束在右臂一侧的大型狙击枪，枪管泛着明晃晃的冷光。", [880] = "你太慢了！", [890] = "什么……？！", [900] = "智尚未扣下扳机，安冬妮娜手中的控制终端迅速展开了全息面板，直连到帕斯卡的心智系统后，开始以毫秒计的速度运行着输入程序。", [910] = "谢谢你的直接植入，就在那一点点时间里……", [920] = "我已经大致明白如何修复它带来的破坏了！", [930] = "……紧急修复补丁？！", [940] = "呵，以前的每一次失败还没给足你教训么？", [950] = "可别小看了眼前的人形啊。", [960] = "白色帽檐下，女孩的嘴角向上扬了一扬。", [970] = "想要赢过我们，还早着呢。", [980] = "对付你们这些病毒……我可是专业的。"}
