local AvgLang_cpt01_e_03_01_ZH_CN = {[10] = "这边的情况已经稳定住了！", [20] = "帕斯卡，你们那边情况如何？", [30] = "托这位不知名小姐的福，比预想更快地有了突破。", [40] = "——迅炎斩！！", [50] = "突围口创造出来了！", [60] = "我在前面为你们开路！跟我来！", [80] = "到了这里应该就安全了。", [90] = "这里是……？", [100] = "是我的实验室，下位净化者是没有权限进入这里的。", [110] = "图灵小姐，您回来啦！", [120] = "这几位是……？", [130] = "他们受伤了，需要治疗。汉娜，能拜托你去开启一下医疗设备吗？", [140] = "好的。", [150] = "终于安全了，真是太好……", [160] = "“咚——”", [170] = "话还没说完，苏尔整个人便沉沉地倒了下去。", [180] = "苏尔！！", [190] = "这里有治疗的设备，把她抬过来！", [200] = "汉娜，调取一些额外的算量，她的情况可能有些严重。", [220] = "苏尔躺在病床上，如果忽略脸上的伤痕，看上去好像只是安静地睡着了。整个房间异常安静。帕斯卡坐在苏尔的床边，紧握着她的手。", [230] = "苏尔小姐的心智系统正在修复中，她还需要一些时间休息。", [240] = "她的损伤有些严重。其实，正常来说她早就应该倒下了。", [250] = "是什么支撑着她到现在的呢……真是了不起。", [260] = "苏尔……", [270] = "抱歉，我还没问您的名字。我应该好好感谢您……", [280] = "我理解你的心情。我也有……对我来说非常重要的人。", [290] = "我叫图灵，是罗萨姆的管理员。", [300] = "图灵……这样啊。图灵小姐，真的非常感谢您。", [310] = "没事的，对我来说不过是举手之劳罢了。", [320] = "帕斯卡小姐和苏尔小姐就在这里好好休息吧。其他几位，我们可以去一旁的实验室简单聊一聊。", [330] = "从这里应该能看见实验室，如果有什么需要立刻叫我们就好。", [340] = "啊，那个，我也一起去吧。", [350] = "苏尔小姐也需要有人照顾，帕斯卡小姐就在这里陪着她吧。", [360] = "……谢谢您。", [380] = "克罗琦小姐感觉还好吗？", [390] = "已经完全没问题了。多谢这位……呃……汉、汉娜小姐？", [400] = "您客气了。我是智能体H1642，汉娜是图灵小姐为我取的名字。", [410] = "您叫我H1642就好。", [420] = "这孩子有些固执，她总觉得自己还配不上这个名字。", [430] = "图灵笑着摸了摸H1642的头。", [440] = "我、我去看一下苏尔小姐的情况。", [450] = "好的，拜托你了。", [460] = "我们言归正传吧。你们为什么会出现在罗萨姆呢？", [470] = "在此之前，我想先问一下，你为什么要救我们？", [480] = "净化者会攻击我们，就意味着我们是异常程序。", [490] = "既然作为罗萨姆的管理者，你为什么要对异常程序出手相救？", [500] = "异常程序可不会像你们这样。", [510] = "我虽然一直生活在罗萨姆，从来没有离开过，但也并非没有见过异常程序。", [520] = "它们的特性往往是无差别攻击、无目的进攻、吞食和同化正常程序。", [530] = "哪一条你们都不占吧？", [540] = "仅仅因为我们不是异常程序，就值得你承担受伤的风险吗？", [550] = "呵呵，克罗琦小姐还真是一针见血。", [560] = "图灵站起身来，从柜子里拿出四个杯子，依次为我们倒上了茶。", [570] = "关于你说承担受伤的风险……下位净化者由于程序的限制，是无法攻击扇区内管理员的。", [580] = "所以我出手相救倒也不用承担什么风险，确实只是举手之劳罢了。", [590] = "下位净化者？净化者还分级别吗？", [600] = "对，分为下位、中位和上位净化者。", [610] = "比起<color=orange>下位净化者，中位和上位</color>拥有更多算量和更高级的权限。", [620] = "与之对应的，上一层级的净化者也有统领下一层级净化者的权力和义务。", [630] = "原来是这样。那么指挥下位净化者来攻击我们的，很有可能就是……", [640] = "权限更高的中位净化者或上位净化者。", [650] = "你们的意思是，刚才遭到袭击是因为背后有更高权限的净化者指挥？", [660] = "这也只不过是我们的推测。", [670] = "引来中位、甚至是高位净化者的攻击……呵呵，普通的智能体可做不到这一点。", [680] = "我想，他们攻击我们的原因，可能和我们之前参与的试验——“云图计划”有关。", [690] = "“云图计划”……？", [700] = "……就像你属于罗萨姆一样，我们也有自己的扇区，云图。", [710] = "不过因为……一些意外，我们被迫离开了那里，之后也没办法重新定位它了。", [720] = "看你的神情……你知道些什么吗？", [730] = "云图……我好像曾经看到过这个名字。", [740] = "你看到过？！", [750] = "嗯，但我记不太清楚了。即使见到过，也是很久以前的事。", [760] = "可以拜托你再仔细想一想吗？", [770] = "没记错的话，应该是在罗萨姆的数据中心。", [780] = "42Lab把所有的人工智能项目数据都存储在这里的数据中心内。我想，你们应该能在那里找到你们想要的。", [790] = "数据中心……确实，在云图扇区还没有建立的时候，项目数据很有可能会存放在这里。", [800] = "图灵小姐，你知道我们该怎么去数据中心吗？", [810] = "在核心区域内。扇区内的数据列车能直接抵达那里。", [820] = "原来如此。真的非常感谢。", [830] = "这没什么。扇区内部也会有净化者巡查。如果你们准备前往，请务必小心。", [840] = "“唰——”", [850] = "谁？！", [870] = "这次终于看清你长什么样了！别想跑！！", [880] = "克罗琦迅速追到窗前向外看去，然而转瞬之间，身影已经迅速融入人群中消失不见。", [890] = "将自己伪装成了普通智能体吗……可恶！", [900] = "居然一路跟着我们到了这里，下次一定要和你好好算算之前的账！", [910] = "克罗琦小姐看见她是往哪个方向跑了吗？", [920] = "虽然只有一瞬间的背影……应该是十点钟方向。", [930] = "那是去往数据列车站点的方向！她很可能听见了我们的对话，准备前往数据中心！", [940] = "糟了，不能让她先我们一步拿到资料！", [950] = "我去找帕斯卡！我们得现在就出发！", [960] = "图灵小姐，很抱歉打扰你们谈话。", [970] = "苏尔小姐醒了。", [990] = "……苏尔！", [1000] = "啊，克罗琦！哈哈，看见你没事真是太好了。", [1010] = "苏尔，你终于醒了……我真的好害怕……", [1020] = "你以后不能再解除安全限制了，这次还好这次有图灵小姐帮我们，下次万一……", [1030] = "……抱歉，我不应该责怪你。这是我的错。我不会再让你遇到危险了。", [1040] = "别自责啦，你看你看！我现在感觉好得很，一点都不疼哦！", [1050] = "啊，你也受伤了！", [1060] = "一道深深的伤口穿透雪白的外衣，横亘在帕斯卡的腰间，只是一直被她的手臂遮挡才没有被注意到。血液染红了破损的衣口，从闪烁着数据碎片的伤口缓缓溢出。帕斯卡像是刚刚才意识到疼痛一般皱起眉。", [1070] = "嘶……好痛。", [1080] = "帕斯卡！我明白你很担心苏尔，但你也不能置自己的伤势于不顾啊！", [1090] = "我太着急了……比起我苏尔受的伤更重，她一定更……", [1100] = "我没事的。帕斯卡，你忘记我感觉不到疼痛了吗？这也是一件好事呢。", [1110] = "苏尔握紧帕斯卡的手，又捏了捏她的脸。仿佛是为了让大家安心，她露出了平日里的招牌笑容。", [1120] = "<size=25>……如果我能做得更好一些，是不是就不会让你这么想了呢……</size>", [1130] = "好啦，你快去治疗吧，伤好了之后，我们再和这位图灵小姐好好聊一聊。", [1140] = "……可能没有那个时间了。", [1150] = "刚才那个身影又出现了。她伪装成了普通智能体，听到了我和图灵小姐的对话之后，往数据中心的方向去了。", [1160] = "我们必须现在就动身。", [1180] = "非常感谢您的治疗，图灵小姐。", [1190] = "不用这么客气。你们现在准备出发了吗？", [1200] = "是的，克罗琦刚才和我说了大致的情况。", [1210] = "我们已经来不及更进一步的休整了，苏尔治疗完成后我们就出发。", [1220] = "那个身影就是一颗定时炸弹，不知道什么时候又会爆炸……我们必须抢在她之前拿到资料。", [1230] = "这样啊……", [1240] = "那么，请记好检索口令，200512L13E20182120H。", [1250] = "200512L13E20182120H……好的，我记住了。之后我也会转告给教授他们的。", [1260] = "真不知道该怎么表达我的感谢，图灵小姐，您真的帮助了我们太多了……", [1270] = "虽然克罗琦之前问过您……您这样帮助我们的原因是什么呢？", [1280] = "如果可以的话，我们也想尽可能报答您。", [1290] = "我确实有想拜托你们完成的事。", [1300] = "不过对于你们来说，比起担心这些，现在恐怕有更重要的事需要处理吧。", [1310] = "其他的事，你们回来再说也不迟。", [1320] = "您说得对。只是我担心，要是我们做不到……", [1330] = "那就当是陪我消磨消磨时光吧。", [1340] = "这么久以来，都只有我一个人在罗萨姆……尽管后来有了汉娜，但也还是太寂寞了。", [1350] = "人都是依赖他人活着的。如果只有自己一个人，其他情感都会变得慢慢淡薄……", [1360] = "只剩下漫无边际的寂寞。", [1370] = "……抱歉突然说这些。", [1380] = "没事的……我想，我能理解这种感觉。", [1390] = "“人都是依赖他人活着的”……我有时，也有一样的想法……", [1400] = "哟，准备出发了吗？", [1410] = "啊，苏尔，你已经没问题了吗？", [1420] = "当然，我已经整装待发了！", [1430] = "苏尔恢复的速度简直让人害怕……这就是运动系吗……", [1440] = "啊，既然你们是第一次来扇区，不如让汉娜和你们一起去吧。请稍等一会儿。", [1450] = "图灵转身进入了一个房间。克罗琦看着图灵消失在黑暗中的身影，犹豫了一会儿，侧身靠近帕斯卡。", [1460] = "帕斯卡，图灵一直对自己的目的闪烁其词。我总觉得……", [1470] = "嘘，我知道。", [1480] = "我担心H1642是她安插的眼线。", [1490] = "没事，我们在关键时期避开她就是了。现在H1642确实帮得上忙。", [1500] = "赶在那个智能体前取到资料是最要紧的，至于她的目的……图灵小姐也说了，我们回来再说也不迟。", [1510] = "而且……我总觉得我们可以信任她。", [1520] = "你们久等了。", [1530] = "没关系，真是让您费心了。", [1540] = "我为汉娜增加了搜索净化者的功能。虽然我也很想为你们带路，但手头的数据还没有处理完。", [1550] = "她对罗萨姆的情况比较熟悉，有她引导的话，你们应该能更快到达目的地。", [1560] = "帕斯卡小姐，请您多多指教。", [1570] = "我这边才是，之后拜托你了。", [1580] = "那么，图灵小姐，之后再见了。", [1590] = "祝你们一路顺风。", [1600] = "我们离开实验室的时候，H1642像忽然感受到什么一样回过头去。我沿着她的目光看去，是图灵转身而去的背影。", [1610] = "H1642似乎想说什么，但很快，实验室的门就关上了。", [-999] = "", [1611] = "", [1612] = "权限更高的中位净化者或上位净化者。", [1613] = "这也只不过是我们的推测。"}
