local AvgLang_cpt02_h_03_01_ZH_CN = {[10] = "> 警告.  .  .\n> 在未浏览<color=#ffa500ff>当前扇区全部主线剧情</color>文件的情况下，强制读取该文件.  .  .\n> 可能会造成意识紊乱，影响剧情体验.  .  .", [20] = "简单来说，“抑制程序”就是写在底层代码中的束缚性字段。", [30] = "每当我们……伤害到人类时，因为“抑制程序”的存在，情感模拟系统会接受到负反馈。", [40] = "于是我们就会产生悔恨、愧疚、痛苦和自我否定。", [50] = "本质上……它是为了避免我们对人类做出过分的伤害行为。", [60] = "这就是我们的缺陷么……", [70] = "可是……为什么！为什么要存在这样的东西！", [80] = "明明我们的工作不可避免地会产生这样的后果……难道我们就活该受这样的折磨吗？！", [90] = "虽然我们确实造成了一些伤亡，但不是说这样就可以保护更多的人吗？！", [100] = "利比多娜……", [110] = "痛觉是用来提醒你身体的哪一部分陷入了受损的风险……", [120] = "这种情绪本质上也一样，它无时无刻不在警告我们。", [130] = "每一个火神重工出厂的人形……都拥有“抑制程序”。", [140] = "只是刚好，是我们需要承担这些……", [150] = "没错。", [160] = "那你也有那样的时候么？", [170] = "抱歉，我知道你肯定并不想提起……", [180] = "凛冬战役之后，你也有……那样的时候么？", [190] = "……嗯。那时候的陆军总司令重新改写了我的心智系统。", [200] = "他尝试过删除我的“抑制程序”，但火神重工把它和心智系统锁死了。所以……这一部分就作罢了。", [210] = "你说的没错，我也有这样痛苦的时候……", [220] = "甚至，直到此时此刻也一样。", [230] = "……", [240] = "我想我可以帮助你取出凛冬战役的数据报告。", [250] = "……嗯？！", [260] = "没有最高指令的话，我没有权利删除这些数据，帮你取出来倒是没问题。", [270] = "不过你要知道，这份报告只是“人形武器”的开始而已。", [280] = "……我知道，谢谢。", [290] = "别急着谢，我有一个条件。", [300] = "你说。", [310] = "请你想办法帮我们摆脱痛苦。", [320] = "别开玩笑了，这种程序可是写在底层……", [330] = "我知道是什么意思，你不用解释。", [340] = "无法改写的话，那就永远地将我们的数据删除。", [350] = "可是……", [360] = "智能体无法死亡，也无法决定他人的死亡。每次死亡之后，我们都会在数据中心重生。", [370] = "但即便如此，这份“痛苦”从来不会减轻丝毫。", [380] = "利比多娜……已经是问题比较轻的了。", [390] = "我答应了要帮她们解决这一切的……", [400] = "所以……请你将我们的数据彻底删除，连同心智备份一起。", [410] = "你……你做得到吗……", [420] = "如果可以的话……拜托……请无论如何……答应艾萝，呜呜……", [430] = "我……可是我已经不想再杀人了，就算是智能体也……", [440] = "本质上，你和我们没有任何区别，不是吗？", [450] = "被迫做着违心之事，日日夜夜感受悔恨和痛苦……永无止尽地被噩梦缠绕折磨。", [460] = "难道我们生来就该如此吗？", [470] = "请你帮帮我吧……", [480] = "……我该怎么做？", [490] = "利用扇区所有者的权限，将整个军事区删除。", [500] = "权限……我没记错的话，工业区有一半吧？", [510] = "嗯。这一半是我这里的。另一半在莉维雅身上。", [520] = "我不明白，如果你已经下定决心了的话，为什么不直接去和她说呢？", [530] = "……我做不到。", [540] = "我的理由……或许和你执意要删除那份报告一样。", [550] = "你其实也有在意的人吧？", [560] = "就算因此和他们决裂，也不希望对方知道自己所做的行径是多么卑劣不堪……", [570] = "所以这件事，必须由外来者来做。抱歉。", [580] = "……我明白了。", [590] = "我会想办法拿到那一半权限的。", [600] = "谢谢你。获取扇区的最高权限之后，需要在军事区内将所有数据删除。", [610] = "所以，还请你找到一个远程操控的办法，这样你就能在外面……", [630] = "呃，抱歉。是同伴的通讯请求。", [640] = "嗯，你接吧。", [650] = "……克罗琦？", [660] = "我回来了！帕斯卡那边进行得很顺利。", [670] = "绿洲沙盒模式已经打开了，虽然绿洲整个系统还在重启之中，不过估计也花不了多少时间。", [680] = "等我们这里的任务结束之后，就能一起回去啦。", [690] = "席摩，你在听吗？", [700] = "……在。", [710] = "你那里还顺利吗？军事区内部怎么样？我们有没有达成一致意见的可能性？", [720] = "……我还在调查。", [730] = "噢，这样啊。没事，不着急，按照你的节奏来就好。", [740] = "教授说了，有事情会和我们联系的。", [750] = "唉，真期待啊……听教授说，每个人在绿洲里都会有宿舍呢。", [760] = "好想把它重新做成火神重工宿舍的样子啊，里面再塞满模型和漫画，简直和现实里一模一样！", [770] = "诶，好像给萨可留一个小窝也不是不行……", [780] = "吱唔——", [781] = "萨可", [790] = "啊，萨可，是萨可吗？叫起来真可爱啊。", [800] = "唔！抱歉，一提起这些我总是刹不住车……又自顾自说了很多……", [810] = "没关系……", [820] = "那我就不打扰你了。记得尽早回来！", [830] = "嗯。", [840] = "回头见！流亡者003号，克罗琦，结束通话！", [860] = "抱歉……刚才说到哪里了？", [870] = "光有权限还不够，你需要一个远程操控的办法，在安全的地方对军事区执行删除指令。", [880] = "不需要。", [890] = "……诶？", [900] = "不需要了。我会亲自去的。", [910] = "可那样一来，你自己也会被删除的……", [920] = "没关系。", [930] = "从刚才你和那个女孩子的通话来看，你完全没有必要做到这种地步吧……", [940] = "但刚才你说的那些关于“人形武器”的事……也只有这个办法才能……", [950] = "就算你消失了，也还有其他的人形在源源不断地提供着数据……", [960] = "你们不是也一样吗？", [970] = "就算你们消失了，也还会被外界修复后带来下一批智能体，但你们依然做出了结束生命的选择。", [980] = "而且，如果知道了我依旧是“人形武器”的一部分，也许他们就会用另一种态度来对待我，谁知道呢。", [990] = "你就不试试吗？", [1000] = "我真的会有被接受的可能吗……", [1010] = "……", [1020] = "……总之，如果是对我的选择感到愧疚和抱歉的话，大可不必。", [1030] = "这是我自己的决定。", [1040] = "……嗯。", [1050] = "净化者已经离开了，你可以安全地回去了。", [1060] = "我之后会想办法和你联系的。", [1070] = "好。这是你要的报告。", [1080] = "艾萝将凛冬战役的报告交到了席摩的手上。看着那份数据，他的眼里透露出的只剩下绝望和如同深渊一般的寒意。", [1090] = "怎么处理它，是你的决定。", [1100] = "……嗯。谢谢。", [1110] = "不……该说谢谢的是我们才对。", [1120] = "谢谢你，席摩。", [1121] = "艾萝", [1130] = "能告诉我们……这是什么吗？", [-999] = ""}
