local AvgLang_cpt03_h_03_01_ZH_CN = {[10] = "> 记忆数据文件编号：HELIOS77523\n> 数据来源：人形，巧可.  .  .", [20] = "<i>拜启，\n亲爱的教授、环球万事服务的大家，以及奥利弗·邦加德老先生。</i>", [30] = "<i>我不知道这封信是否有机会送出，因为我已经完全失去了和现实世界的联系，\n但我相信，一定会有人来救我们。</i>", [40] = "<i>所以，我会一直保持写信的习惯，记录云端上的点点滴滴，直到离开这里，然后将发生的一切讲给大家听。</i>", [50] = "<i>此时此刻，我正在自己的甜品店里，一边沐浴着阳光一边写下这封信。曾经追杀过我的净化者，正从我的窗外走过。</i>", [60] = "<i>难以相信，我的第一个甜品店竟然是在云端开张的。我给它起了一直想要的名字——可可屋。</i>", [70] = "<i>就在昨天，我还遇到了希安，现在她和我一起在这里生活。</i>", [80] = "<i>扇区的管路员塞西小姐，是一个很温柔的人。她帮助我们修改了特征码，我们才能在这里生存。</i>", [90] = "<i>新的生活充满希望。虽然还是很想念大家，但是赫里奥斯的智能体对我和希安都十分热情。</i>", [100] = "<i>可可屋很受大家欢迎，每天都会迎来很多客人。</i>", [110] = "（敲门声）", [120] = "来啦！请稍等。", [130] = "<i>请你们不要为我担心，我相信我们很快就能再见面的。巧可敬上。</i>", [140] = "请问您要来点什么呢，啊，今日的特别推荐是“Diya甜蜜礼盒”哟。", [150] = "果然是甜品店！好厉害！", [160] = "一个手持“餐具”，扎着双丸子头的少女走了进来。", [170] = "巧可很快就意识到，这个看起来和自己一般大小的少女，浑身上下散发着令人窒息的强大气场。", [180] = "那个……我们不支持自带餐具……的说。", [190] = "哇！好特殊的智能体，好可爱！！！", [200] = "你叫什么名字？我是贝欧尼斯，你一定听说过我吧，嘻嘻。", [210] = "对了对了，你们智能体用代号作名字太没劲了，不如我给你取个好听一点的！", [220] = "巧克力，巧克力……巧可！巧可怎么样？", [230] = "你、你是上位净化者……", [240] = "好！从今往后你就叫巧可了！", [250] = "那么我要点餐咯！", [260] = "等、等一等——", [270] = "贝欧尼斯兴奋地在货架前瞅来瞅去。", [280] = "这个看起来不错！那个看起来也很好吃！啊啊怎么办，到底要选哪个呢？", [290] = "呜哇，好纠结，但是好快乐啊！", [300] = "唔！", [310] = "贝欧尼斯凑到巧可的脸上。", [320] = "怎、怎么了？", [330] = "有什么推荐的吗？比如，你最拿手的作品——啊不行，必须要循序渐进地享受快乐……", [340] = "那个……不如试试我们的新产品，也是今日推荐的礼盒，怎么样。", [350] = "快拿来快拿来！", [360] = "贝欧尼斯一屁股坐到凳子上，兴奋地摇着头。", [370] = "请、请慢用……", [380] = "这、这些是什么？好厉害！", [390] = "Diya甜蜜组合，一共十二种口味，分别是醇黑巧克力、覆盆子巧克力、玫瑰松露，还有……", [400] = "未等巧可说完，贝欧尼斯就迫不及待地拾起一块醇黑巧克力丢进嘴里。", [410] = "！！！", [420] = "唔——", [430] = "好好吃！！！", [440] = "谢、谢谢夸奖……", [450] = "贝欧尼斯一个接一个地吃完剩下的十一种巧克力，每吃一口，她都会用双手捂着脸颊，不住地摇晃着。", [460] = "竟然能让净化者喜欢，有一点得意呢，嘿嘿……", [470] = "没想到赫里奥斯竟然有这么好玩的地方！", [480] = "原来还觉得这个扇区留不下什么东西，真是没劲——", [490] = "不过现在，我因为这家甜点店爱上这个扇区了！以后我会经常来的！", [500] = "这可不行！", [510] = "嗯，为什么？", [520] = "……不，我的意思是……欢迎常来。", [530] = "嘻嘻嘻，从今往后，这家店归我罩着啦！", [540] = "要是有邪灵威胁到可可屋，我一定不会放过它们！", [550] = "哦、哦——", [560] = "对了，还有什么推荐的口味呢？", [570] = "我、我这就拿菜单。", [580] = "（黑屏过场）", [590] = "<i>拜启，\n亲爱的教授、环球万事服务的大家，以及奥利弗·邦加德老先生。</i>", [600] = "这些我就带走啦，嘻嘻！", [610] = "好、好的。", [620] = "用什么支付呢……这个扇区的货币？", [630] = "啊，不不不，对你来说，应该算量更加有用吧？可以吗？", [640] = "可、可以。", [650] = "——OK，查收一下吧。", [660] = "诶，这么多？", [670] = "嘻嘻，就当是小费啦，谢谢你的招待！", [680] = "那我就回去啦，拜拜！", [690] = "把包裹挂在武器上飞走了呢……", [700] = "<i>你们可能不相信，那些追杀过我的净化者……</i>", [710] = "<i>他们之中的上位净化者——贝欧尼斯，竟然成为了可可屋的VIP。</i>", [720] = "往后的日子会怎么样呢……"}
return AvgLang_cpt03_h_03_01_ZH_CN

