local AvgLang_cpt05_e_04_01_ZH_CN = {[10] = "病毒依然猛烈，我们合力抵抗也无法阻止它们的疯狂进攻。", [20] = "大家小心，千万不要接触到它们！", [30] = "真是难缠啊……", [40] = "克罗琦用巨盾将一只无法辨识模样的病毒砸了个稀烂——这下更加无法辨别它的样貌了。", [50] = "苏尔！", [60] = "另一边，苏尔正在用力地甩着阿尔瓦克，试图将一块缠在上面的胶状病毒甩掉。", [70] = "呜……快走开！快走开啊！", [71] = "苏尔", [80] = "一只弩箭破空而来击中病毒的核心，后者终于松开了纠缠，无力地摊到地上。", [90] = "Thank you，席摩！", [100] = "小意思啦！", [110] = "病毒蜂拥而上，克罗琦的艾吉斯号喷出火舌，机兵轰鸣过后留下一堆狼藉，很快又被覆潮所湮盖。", [120] = "这些东西难道没有“性命”的概念吗，疯了似的往前冲。", [130] = "它们没有个体意识，认知范围内甚至只有“攻击”这一种行为。", [140] = "听上去也挺惨的……", [150] = "我正在试图分析它们的构造，再给我一点时间……", [160] = "你们快看，它们中间好像有一个不一样的家伙！", [170] = "在哪？", [180] = "顺着席摩所指的方向，我似乎看到一个身影在黑色的覆潮之中一闪而过。", [190] = "我没有看到啊。", [200] = "诶？难道是我看错了……那个影子看起来很明显和其它的熵不同。", [210] = "也许是病毒聚合在一起形成的堆积块。", [220] = "噫，好恶心！", [230] = "我们的行动范围在缩减，各个方向都要注意防守。", [240] = "安娜，分析完成了吗？", [250] = "马上——", [260] = "有一个终端正在源源不断地向熵发送着指令：不惜一切代价占领花园。", [270] = "等等……", [280] = "不，不能让它们毁掉我们的花园！", [290] = "似乎是因为安冬妮娜分析的指令结果，愤怒的奥吉里自顾自地向前方冲了出去。", [300] = "奥吉里！那里危险！", [310] = "看到脱离阵型的奥吉里，一处的熵迅速聚合增殖，顷刻间，硕大的异体从黑潮之中浮现而出。", [320] = "异体冲向了奥吉里，在行进的过程中愈发扩张，直到身躯如海啸般遮天蔽日。", [330] = "奥吉里——", [340] = "几乎是在同一时刻，安冬妮娜冲向奥吉里，死死地抱住了她。", [350] = "呜！！！", [360] = "安冬妮娜的防护进程没能阻挡住异体的攻击，二人被巨大的冲击力震退。即便是摔向地面时，安冬妮娜依旧死死地将奥吉里护在身后。", [370] = "安娜——！", [380] = "席摩！掩护她们！", [390] = "明白——", [400] = "我们的攻击同时落在异体身上，异体散落分裂，嚎叫着重新汇入病毒群中。", [410] = "我们放弃了阵地冲到安冬妮娜和奥吉里的身前。", [420] = "你们没事吧……呜！", [430] = "咳咳……奥吉里交给我。", [440] = "安冬妮娜！", [450] = "我……", [460] = "安冬妮娜！安冬妮娜！糟了，她昏过去了！", [470] = "抱歉……抱歉……安冬妮娜！都怪我……", [480] = "必须得快点转移才行！", [490] = "我们必须撤回花园再想办法了……", [500] = "那……那花园会不会……", [510] = "不……不能那么自私，奥吉里……", [520] = "席摩，克罗琦，麻烦你们开路！", [530] = "我们向花园内撤退，临走之前，我再次看向那些蠕虫般扭动的病毒。", [540] = "一个暗色的身影被病毒所环绕，虽然看不清样子，但透过它的轮廓，我似乎依稀看见了人影。", [550] = "倏尔，它再次被淹没在黢黑的潮水之中。"}
