local AvgLang_cpt00_e_01_01_ZH_CN = {[10] = ">   看得到吗？.  .  .", [11] = "我", [20] = ">   现在在你眼前的是预备传输界面，不过这些你不用管，交给我处理就好。\n>   按照你要求的，我会把云端备份数据重置到那场事故发生后的一段时间.  .  .", [30] = ">   我再重申一遍，这次尝试里，我会让你同步那位教授的意识系统。\n>   简单地说，你可以用自己的意识来代替他的意识.  .  .", [40] = ">   那位教授尽了全力，才把那些人形送回现实。\n>   可惜她们完全失去了这段记忆，也帮不上你多少忙.  .  .", [50] = ">   能不能把剩下的人形心智，还有那位教授的意识带回来，就看你了.  .  .", [60] = ">   至于我是怎么做到这些的，你就别问了。多给我留一些底牌.  .  .", [70] = ">   对了，那位帕斯卡，和她好好相处。\n>   虽说是我的订制型人形，但不同的经历已经让她获得了自己独特的想法和意识。\n>   记得别把我和她划等号.  .  .", [80] = ">   等你回了格里芬之后，还请把发生的所有事情告诉我。\n>   你的这段经历，会成为16Lab珍贵的资料。放心，我会保密的。\n>   另外，记得别暴露自己的身份.  .  .", [90] = ">   那么，好运，指挥官.  .  .", [100] = "……", [110] = "……正在连接至麦戈拉云服务器。", [120] = "……意识系统同步完成。", [130] = "(EST: 16:42, 24/12/2058) 意识上传成功.  .  .", [140] = "侦测到威胁程序。目标数量：3。危险等级：高。", [141] = "未知程序", [150] = "定位（α 79.65, β 83.73, γ 83.00），增援请求已发……", [160] = "**！没完没了的！", [170] = "帕斯卡，你身后！", [180] = "唔……糟糕！", [190] = "启动进程被打断了！", [200] = "你没受伤吧？", [210] = "我没事。我们预估错误了，现在没法启动<color=#ffa500ff>沙盒模式</color>。", [220] = "我们两个的心智算量加起来还不够吗？", [230] = "还差了一点。", [240] = "那现在怎么办？教授，别愣着！快来搭把手！", [250] = "（等等，战斗？呃，看来同步的时机并不太好。）", [260] = "现在是什么情况？", [270] = "您的意识不稳定了吗？", [280] = "这种要紧关头，别出现新问题啊……", [290] = "小心！", [300] = "教授，说真的，您没事吧？", [310] = "敌人太多了，我们可能没法兼顾你。", [320] = "（兼顾？不太需要吧？我可以处理的，我遇到过很多这样的状况。）", [330] = "明白了，我来对付侧翼的敌人，正面交给你们。", [340] = "嗯，了解。", [350] = "苏尔，坚持一下，我已经呼叫支援了。", [360] = "<color=#ffa500ff>克罗琦</color>和<color=#ffa500ff>席摩</color>正在赶来的路上。", [370] = "太好了，加上他们的算量，一定足够我们启动<color=#ffa500ff>沙盒模式</color>了。", [380] = "总之，为他们两个争取一些时间就好。", [390] = "教授，战斗期间的支援就拜托您了！"}
