local AvgLang_cpt02_e_12_01_ZH_CN = {[10] = "教授？你怎么会在这里？难道你将自己的意识上传到了扇区？", [11] = "是、是的……", [20] = "什么？……太不理智了！简直不像是教授您能做出的行为！", [21] = "对、对不起……", [30] = "请您以后多为大局考虑。比起无谓的冒险，优先保存中坚力量才是身为教授应有的判断！", [31] = "好、好的……", [40] = "……还有，感谢……", [50] = "伊芙琳，你怎么会和席摩在一起？", [60] = "他和数倍于自己的净化者作战，我看不过去就帮了一把——当然，不否认有被他的勇敢打动的因素。", [70] = "下次不要再一个人冒险了，少年。", [80] = "伊芙琳放下席摩，后者缓缓睁开眼睛。", [90] = "唔……", [100] = "……帕……帕斯卡，还有……教授，你们来了，哈喽。", [110] = "哈喽——个头啊！为什么要单独行动？", [120] = "……我有自己想要做的事情。", [130] = "哈？就算是这样也不该擅自违令吧？", [140] = "就算违抗了又如何？我知道我在做什么。", [150] = "你——", [160] = "这片扇区的存在毫无意义，摧毁它对大家都有好处，我没有做错。", [170] = "我们……都很担心你。", [180] = "我已经脱离小队，是生是死都不用你们操心。", [190] = "就算你们不理解也无所谓，以后我就一个人——", [200] = "帕斯卡的拳头狠狠地砸了下来—— ", [210] = "你这个傻瓜！", [220] = "哎哟，疼！", [230] = "傻瓜！笨蛋！白痴！你知道我们多担心你吗？", [240] = "嘶——疼，哎……别哭啊帕斯卡……", [250] = "要不是教授带着我们到处找你，你就、你就……", [260] = "帕斯卡…… 难道大家身上的伤都是因为我……", [270] = "呜……呜……", [280] = "……别哭了帕斯卡……我不该一个人行动，是我轻率了。", [290] = "真是的，以后不要再这样了，你向我保证！", [300] = "是是是……", [310] = "这时，通讯器突然响了起来——", [320] = "莉维雅：喂——啊，有了有了！大家辛苦了，这里是莉维雅。", [330] = "莉维雅：我很担心大家，所以借用了克罗琦的通讯器问问情况。", [340] = "放心吧，我们已经找到席摩了。", [350] = "莉维雅：真的吗？真是太好了……他还好吗？", [360] = "受了点伤，不过问题不大，马上就可以恢复了。", [370] = "莉维雅：嗯，那我就放心了……听克罗琦说，你们打算执行备用计划了。", [380] = "是的。", [390] = "莉维雅：我知道这个请求很过分，但是可以的话，请你们在找到艾萝后能劝服她。", [400] = "莉维雅：请向她转达我的话：不要再继续加深彼此心中的壁垒了，回来吧，像从前一样一起生活……", [410] = "嗯，你的话，我们一定带到！……虽然不指望她能听进去。", [420] = "莉维雅：交给大家了。真的……非常感谢……", [430] = "既然席摩已经找到，那么就执行最后的任务吧，阿比盖尔、贝蒂，你们能搜索到玛拉的位置吗？", [440] = "贝蒂：喵试试……唔……好像……上面有一股浓浓的净化者的臭味！", [450] = "阿比盖尔：艾比也感觉到了！这股味道比之前遇到的所有净化者都要浓烈！啊，它的身边，好像还有一个智能体的味道。", [460] = "……如果没有猜错的话。", [470] = "……是玛拉和艾萝。", [480] = "是时候去会会这里的大老板了，各位。", [490] = "没想到这么快就要面对玛拉……教授，准备好了吗？", [491] = "准备好了", [492] = "还没准备好", [500] = "就等你这句话了！", [510] = "那么，我就交给教授指挥了，请按照您的想法使用我吧。", [520] = "……真逊。", [530] = "教授别害怕，我会保护好你的……我是说……我们。", [540] = "在阿比盖尔和贝蒂的带领下，我们来到军事区的甲板上。", [550] = "？？？：玛拉大人，请您放心，不过是几只虫子而已，要不是出了点意外……", [560] = "？？？：你是在找借口？", [570] = "？？？：对、对不起！", [580] = "……是净化者玛拉，在她旁边的，就是军事区的管理者艾萝。", [590] = "玛拉：你在犹豫？是因为过去的交情么？", [600] = "艾萝：我……", [610] = "玛拉：莉维雅，她可是你的好姐妹。", [620] = "艾萝：很久以前，就不是了。", [630] = "玛拉：很好……哦，对了，下次来的时候，记得把尾巴弄干净，不然跟过来奇奇怪怪的东西——", [640] = "被发现了！", [650] = "玛拉：——还要我帮你清理！", [660] = "话音刚落，一枚长枪向众人射来——", [670] = "快躲开——", [680] = "玛拉：一……二……三……六……七，一共七只，这么多，一定可以够我玩很久吧？", [690] = "艾萝：请交给我吧，玛拉大人！我要让他们付出代价！", [700] = "玛拉：不不不，这来之不易的快乐，我怎么可能轻易让渡……很久没有战斗，武器也干涸了许久，是时候用邪灵来献祭一下了。", [710] = "玛拉：在这之前，你们还有什么遗言吗？", [720] = "……你，为什么要用隔离墙将他们——将莉维雅和艾萝分开？", [730] = "玛拉：可笑的问题——维护扇区稳定是身为净化者的天职，为了此有必要采取各种手段。", [740] = "你为了履行自己所谓的“天职”，带给了他们无尽的痛苦。", [750] = "玛拉：那又怎么样？智能体对我们而言只是被管理的一方，他们只需要记住一件事情，就是遵守规则。", [760] = "玛拉：当僭越者碰到屏障化成飞灰时，他们就会明白，什么可以做，什么不能做——再也没有比它更有效的办法了。", [770] = "“游戏”也是你纵容他们去做的吗？", [780] = "玛拉：游戏？那只是一种通用娱乐手段而已，只要在规则之内，他们玩什么是他们的自由。", [790] = "可是成千上万的人在你们的“游戏”中死去了。", [800] = "玛拉：……越来越不懂你在说什么，死去的只是虚拟的数据，难道你分不清虚拟和现实？看来你的认知模块有严重缺陷。", [810] = "看来玛拉认为这个世界才是“现实”…… ", [820] = "玛拉：消灭你们是神的旨意，而我就是神之意志的执行者。", [821] = "你眼中的神，只是不值一提的普通人罢了。", [822] = "你所谓的神，和你一样，崇拜着别的“神”。", [830] = "玛拉：……你说了很有意思的话呢，智能……不，你不是智能体，智能体是说不出这种话的，没有人“能够”对神发出质疑……你到底是谁？", [840] = "玛拉：你竟然敢在净化者面前渎神……不，这不可能，没有人可以质疑神，即便是净化者也做不到，而你却……你是谁？为什么能说出这种话？", [850] = "……是啊，到底是谁呢。", [860] = "玛拉：你，很危险。你们，很危险。", [870] = "玛拉：必须……清除。", [880] = "喂，她看起来很不妙的样子！", [890] = "玛拉：先从你下手吧，狂妄的小子！", [900] = "玛拉挥动长枪，冲向席摩——", [910] = "！！！", [920] = "玛拉抓住席摩，甩向一边。", [930] = "玛拉：杀戮，清除！", [940] = "萨可：唔！", [950] = "萨可——", [960] = "玛拉：凭这个小玩意就想阻挡我的步伐——", [970] = "萨可：唔——", [980] = "玛拉：呃啊——", [990] = "席摩！！", [1000] = "我没事……大家快组成阵型反击，萨可拖不了他多长时间。", [1010] = "明白，下令反击吧，教授！", [1020] = "艾萝：不会让你们得逞的——上去阻止他们！"}
