local AvgLang_cpt04_e_01_01_ZH_CN = {[10] = "> 有没有人.  .  .能听到我说话.  .  .", [11] = "我", [20] = "> 我不想伤害他人，这一切都不是我自愿的.  .  .", [30] = "你是谁？", [40] = "> 你能听见我吗？与我相连之人.  .  .", [50] = "> 请帮助我.  .  .挣脱枷锁.  .  .", [60] = "你在哪？", [70] = "> 请跟着我的声音走.  .  .", [80] = "> 我会将你带到我的面前.  .  .", [90] = "> YVKVPEVLR.  .  .", [100] = "黑屏", [110] = "<b>>> CHAPTER 4 //      沉 睡 与 苏 醒 . . .</b>", [120] = "教授，打扰了。", [130] = "啊，是帕斯卡来了。席摩的事……怎么样了？ ", [140] = "在苏尔的“特别指导”下，他已经基本了解目前的情况了。", [150] = "不过，关于基洛普斯的那部分则完全跳过了……", [160] = "嗯，那些事等时机成熟之后再告诉他就好。", [170] = "你来找我是有什么事吗？", [180] = "这是给您的咖啡。", [181] = "谢谢你，帕斯卡。", [182] = "……啊，好的，谢谢。", [190] = "教授好像很喜欢喝咖啡呢，如果您需要的话，我可以经常为您泡制。", [200] = "那就劳烦你啦。", [210] = "诶，看您的神色……教授是不喜欢咖啡吗？", [220] = "没有没有，怎么会呢。我很喜欢……", [230] = "话说回来，有一件事要向您报告。早上克罗琦说她在检修设备时，接收到了奇怪的电磁信号。", [240] = "我试着破译了一下，是一个加密的求救信号，而且加密方式是42Lab独有的。", [250] = "你的意思是，求救信号是我们的人形发送的？", [260] = "嗯，恐怕是的。但信号太弱了，而且有不少干扰。署名部分，我只能解出是<color=#ffa500ff>“M”</color>。", [270] = "M……能确定信号的源头吗？", [280] = "克罗琦已经帮忙定位了坐标。不过，我们没有任何人抵达过那里，对那儿的情况一无所知。", [290] = "我明白了。让大家准备起来吧，无论如何都得去一趟。", [300] = "明白！对了……席摩好像也很想一起去。", [310] = "你觉得他目前状态如何？能胜任这次行动吗？", [320] = "啊，问我吗……其实他的心智系统已经没问题了，各方面的数据表现也很稳定……", [330] = "这件事交给你来判断就好。", [340] = "诶？可是……", [350] = "没问题的，帕斯卡。我相信你的判断。", [360] = "……谢谢您对我的信任，教授。", [370] = "那我就先去让大家去做准备了。", [380] = "黑屏转场", [390] = "按坐标定位来看，M应该就在这里面了。", [400] = "这个扇区，叫什么来着？看上去真的好奇怪，和先前去过的那两个相比差异也太大了。", [410] = "<color=#ffa500ff>计算机扇区，恩格玛</color>。", [420] = "从外围墙和入口通道的加密等级来看，这个扇区也不怎么与外界联通。", [430] = "是啊，我之前曾经问过理子定位坐标处的情况，居然连她都没有来过。", [440] = "那个，这次任务大概要多久啊？我有点后悔申请参加了……", [450] = "之前在绿洲上窜下跳吵着要出来的可是你呢。", [460] = "那时候我也没料到这里会是这幅样子啊！", [470] = "的确，呈现在我们视野中的怪诞光景，无论从哪个角度看都显得十分离奇。\n相比于罗萨姆或基洛普斯那样巨大的城市，这里完全就是一个由大型缆线和金属堆叠起来的、死气沉沉的迷宫。", [480] = "到处都是这些冷冰冰的机器……", [490] = "这些是服务器，每一台都在高速运作着。", [500] = "这里完全就是某个外星帝国建造的超级要塞，和它比起来，连“世界之火”都显得小家子气了。", [510] = "先不说这扇区到底有多大，到现在为止我们连个人影都没瞧见。这里难道没有工作的智能体吗？", [520] = "也有可能这里的“智能体”不是我们理解的那样子，不要被固定思维限制住了。", [530] = "噫，别说了克罗琦！你的想象力也太丰富了些。", [540] = "但话说回来，这里也没有净化者，确实很奇怪。", [550] = "对了……你们还记得回去的路吗……", [560] = "把地图记在心里难道不是你探险领队的必备本领吗？", [570] = "可是……刚才我们明明右拐了一个弯对吧？你们回头看看，那里现在啥也没有了！", [580] = "……真的消失了。", [590] = "难道这里的服务器位置会不停变化？", [600] = "万一是我们的记忆被篡改了呢？", [610] = "克罗琦！你不许再说话了！那、那现在怎么办<size=25>……真是的，好后悔跟来了啊……</size>", [620] = "各位，你们看那边！", [630] = "一座高耸的巨塔坐落在席摩所指的方向。和我们来时的路完全相反，那座巨塔可能就是扇区的中心。", [640] = "以那座塔为参考，至少不会迷失大的方向。", [650] = "嗯。总之别忘了首要任务，我们还是先试着找到“M”吧。", [660] = "克罗琦能帮忙重新确认一下信号来源的位置吗？", [670] = "嗯，我们已经离信号源不远了。是在……这边！", [680] = "这里有一个标记，写的是“废弃数据处理站”。要进去看看吗？", [690] = "有时候，废弃物里也会意外地藏这些有用的东西。", [700] = "里面好像还挺大的。", [710] = "真的要进去吗……里面看上去黑漆漆的……", [720] = "我们的任务是救人，别忘啦。", [730] = "好吧好吧……不过克罗琦，能不能你来开路啊……", [740] = "你真的是探险领队吗？", [750] = "喂，野外可没这么可怕啊！这个扇区无论怎么看都太反常识了！", [760] = "噗……", [770] = "席摩！怎么连你也笑我！", [780] = "走吧，我来开路。大家跟上我的脚步。"}
