local AvgLang_cpt04_h_01_01_ZH_CN = {[10] = "> 记忆数据文件编号：ENIGMA3619\n> 数据来源：人形，麦克斯.  .  .", [11] = "？？？", [12] = "智能体", [20] = "计算机扇区恩格玛，某个角落……", [30] = "确定真的是这个方向吗？", [40] = "好歹也多信任我一点吧……", [50] = "我可是仔仔细细地搜索过扇区数据分布图，不会错的。", [60] = "数据交换点就在那里。", [70] = "你确定到了那里我们就能联络到外面么？", [80] = "这我可不敢百分百保证，但不去试试怎么知道呢？", [90] = "好吧，但是如果行不通的话，我可不愿意再浪费时间了！", [100] = "嗯？你有后备方案吗？", [110] = "直接闯出去！", [120] = "……认真的吗，麦克斯？", [130] = "凭这个扇区里驻扎的净化者数量，计算后成功率大约是6.27%，自身算量损失高达79%。", [140] = "绝对不是什么划算的买卖。", [150] = "管它百分之几百分之几，我受够了！", [160] = "到现在为止，其他人形我们可是一个都没瞧见！", [170] = "云图扇区到底发生了什么啊？！", [180] = "与其纠结这种暂时无从得知的事，还是集中在眼前的问题上比较好。", [190] = "不远处，显示着“数据交换点”的建筑物终于出现在二人的眼前。", [200] = "终于看到了！嘁，希望它能起点作……呜哇！！！", [210] = "怎、怎么回事！拉姆！你看到了吗？！", [220] = "嗯！数据交换点的建筑突然扭曲了起来！", [230] = "是加载错误吗？还是那个区域遭到了……", [240] = "麦克斯的话音未落，二人见到自己周围的建筑物甚至脚下的道路也开始变得扭曲，随后她们纷纷失去了重心。", [250] = "哇啊啊啊！发生了什么！", [260] = "拉姆！喂——快抓住我——！！！", [270] = "麦克斯——", [280] = "拉姆和她身后的建筑物一同被卷入了暗蓝色的数据漩涡之中。这时麦克斯才发现，整个扇区都陷入了令人无法理解的混乱里。", [290] = "不行，不行！得想想办法！办法……", [300] = "有了！沃登克里夫上有永磁体！用电场来增强模拟重力的话……", [310] = "拜托一定要起效啊！", [320] = "麦克斯手中的电磁手杖“沃登克里夫”冒出了电光，周身模拟范围内的重力恢复了短暂的正常。", [330] = "至少这样可以移动了……", [340] = "接下来得找到拉姆才行……", [350] = "……诶？那些是……智能体吗？", [360] = "喂——你们还好吗？", [370] = "……", [380] = "能听到我说话吗——", [390] = "……", [400] = "难道也是因为失重，连动都动不了了吗？还是被吓坏了？", [410] = "真是的，还得靠近去救她们……还好沃登克里夫制造的重力范围还能覆盖几米。", [420] = "麦克斯艰难地在混乱的数据中向智能体一步一步地走去，但当她来到智能体身边时，眼前的景象令她错愕。", [430] = "喂……醒醒！你们……你们怎么了？！", [440] = "几个智能体脸上的惊恐的神情像是被定格了一般，无论怎样麦克斯都无法唤醒她们。", [450] = "麦克斯尽力调取算量保持沃登克里夫的重力场，试着搜索了附近的范围，但所有能见到的智能体都仿佛被抽走了心智一般。", [460] = "这、这这这到底是什么！失、失魂症吗！", [470] = "不对不对，麦克斯，你要相、相信科学！一定是发生了什么系统错误！", [480] = "呜哇哇哇！呜！！！", [490] = "这声音是……还有人活着吗？！", [500] = "听声音的来源，应该是这一侧才对。", [510] = "救命——救——哇！！！", [520] = "小小的智能体因无法控制自己的身体而惶恐无比，不住地叫喊着，她周围的一切已经全部被身后的数据漩涡卷进去了。", [530] = "坚持住！我来救你——", [540] = "麦克斯移动到小智能体的身边，重力场范围覆盖到后者之后，惊恐的智能体坐在地上嚎啕大哭了起来。", [550] = "呜呜呜啊啊啊啊！", [560] = "啊！别嚷嚷了！！！", [570] = "你已经暂时安全了！给我安静一点！！！", [580] = "呜……呜……", [590] = "混乱越来越严重了，这附近的数据正在流失……", [600] = "呜！已经看不清周围了