local AvgLang_cpt05_e_06_01_ZH_CN = {[20] = "唔！", [30] = "身后，克罗琦！", [40] = "席摩未等克罗琦回头，手中的弓弩已经射出了强力的一箭，漂亮地击中了最后一只准备从背后偷袭克罗琦的净化者。", [50] = "谢、谢谢……要是被抓到免不了要受伤……", [60] = "从密切配合和输出效率上看，它们已经抵得上一支特种突击队了。", [70] = "和以前的那些家伙完全不一样呢。", [80] = "难道是因为这里离云端的防火墙太近，才会调遣这种训练有素的队伍吗？", [90] = "不，虽然花园只占据着庇厄里亚小小的一部分，但我偶尔也会去四周的荒漠搜索一些富余资源。", [100] = "即便偶尔遇见过熵，我也从来没有看到过这么厉害的净化者。", [110] = "奥吉里……你以前见过么？", [120] = "没有……", [130] = "奥吉里低着头，声音中夹带着不甘和委屈。", [140] = "不过这对庇厄里亚来说是好事吧？如果有那么厉害的净化者在，就不用担心熵的入侵了。", [150] = "话是这么说没错……", [160] = "奥吉里，如果可以的话，我们希望立刻能获得庇厄里亚的扇区密钥。", [170] = "诶？很着急吗？", [180] = "并不是急着想要拥有权限或资源什么的……", [190] = "拥有了密钥之后，我们能帮助你们启动沙盒模式。", [200] = "沙盒模式可以从底层上完全隔离非授权访问，还可以从绿洲调用资源加强防御。", [210] = "听起来像是一个一劳永逸的办法。", [220] = "嗯，没错。如此一来，我们也不必耗费多余的精力在净化者身上。", [230] = "我明白了，只要能让庇厄里亚平安无虞，权限这种东西，我们也并不十分需要。", [240] = "我们的密钥放置在花园的博物馆中，请大家和我来吧。", [260] = "哇啊啊啊——！！！", [270] = "苏尔，冷静一点……", [280] = "奥吉里奥吉里！庇厄里亚还有什么漂亮的地方是你没带我们看过的吗？！", [290] = "我简直都想住在这里了！", [300] = "呈现在我们面前的这座“博物馆”并不似想象般恢弘而宽阔，但却是存放着人类数千年文明与造化的时空回廊。", [310] = "从石器时代到信息时代，生命的长河在这里凝固，庄严而安静地等待着它们的造访者。", [320] = "这就是你们的工作吗……", [330] = "哇！这又是什么？", [340] = "苏尔伸出手去触摸一幅悬浮在空中的透明画框。周围的灯光随之骤然暗淡。", [350] = "透明画框中不断地涌出数据，迸发而出的数据渐渐呈现出缤纷的色彩，在我们的正上方汇聚，直至填满整个穹顶。", [360] = "这……这是……", [361] = "苏尔", [362] = "奥吉里", [363] = "安吉拉", [364] = "帕斯卡", [370] = "是文森特·梵高的《星夜》……", [380] = "人类的文明也是如此灿若星河呢。", [390] = "真希望有一天，我们也可以这样……", [400] = "仰望着头顶的《星夜》，我们似乎都明白了些什么。\n保护这个扇区，并不是简简单单地保护资源与数据……", [410] = "即使在其他人看来，庇厄里亚“毫无价值”……", [420] = "但对于我们而言，它却意味着“无上价值”。", [430] = "真的很美呢，连语言也失去颜色了……", [440] = "言语间，我瞥见帕斯卡担心地察看了一眼安冬妮娜。\n后者此时正呼吸平稳地靠在克罗琦的后背，星夜的光辉映在她平和的脸庞上。", [460] = "你们能喜欢庇厄里亚，我真的很高兴。", [470] = "我以为人们已经逐渐沉迷于娱乐带来的感官刺激，再也不会去欣赏艺术了……", [480] = "没想到还有像你们这样……像教授这样愿意仰望星空的人。", [481] = "感谢你们，即使算量无几也愿意守望在这里。", [482] = "我并不了解艺术，但美的事物总能打动人心。", [490] = "诶？……那并不是什么伟大的事，相反，我感到很幸运。", [500] = "能向人类的艺术学习……是一件非常幸福的事。", [510] = "嗯，我也是因为相信这个，才会一直守在这里。", [520] = "人类的艺术也为我们带来了一些与众不同的改变……", [530] = "虽然庇厄里亚并没有和其他扇区有什么数据交流……", [540] = "但如果说“庇厄里亚的算量是被伯班克挪用了”，似乎也并不为过。", [550] = "伯班克？是另一个扇区吗？", [560] = "嗯，伯班克是媒体娱乐扇区。", [570] = "我所说的“挪用”，并不是指云端数据和空间的占用……而是现实世界。", [580] = "至少，庇厄里亚的算量如此之少，也是因为人们日渐沉溺于娱乐信息之中。", [590] = "比起艺术，人类更需要短暂的感官刺激，更需要用源源不断的信息冲击来代替思考……", [591] = "艺术是拯救人们走向娱乐至死的良药。", [592] = "娱乐不是什么沉重的话题，它的存在本身没有错。", [600] = "这也是庇厄里亚存在的价值，人类最后的精神防线。", [601] = "我", [610] = "将人类从娱乐的附庸中解救出来，让他们不再因失去意义的欢愉而感到虚无。", [620] = "能获得您的认可真是太好了……", [630] = "我也希望人类有一天能够重新看到我们，认识我们的价值……", [640] = "对感官刺激的向往并不可怕，“沉溺其中”也只是人类自己的惰性作祟。", [650] = "而艺术也不会因此而消融。", [660] = "没错……不少艺术形式的发展历史，本身就是由下里巴人走向阳春白雪的。", [670] = "娱乐是推动科技革新的动力，我想这一点教授也会同意的。", [680] = "教授的想法确实非常深刻呢。", [690] = "那么我就把这幅《星夜》收回去啦，我们得尽快去取权限密钥才是。", [700] = "啊对，差点把正事给忘了！", [710] = "我们跟随着奥吉里前往博物馆的深处。", [720] = "这里就是扇区权限的存放地了。", [730] = "博物馆的大厅正中，有一株置于玻璃罩中、荧荧发光的植物，在丰茂的绿植之中格外亮眼。", [740] = "奥吉里走到它的边上伸出手去，玻璃罩便消失了。\n接着，植物的光辉渐渐飞散到空中，汇聚到她的手里，化为了权限密钥。", [750] = "请收好，这就是庇厄里亚的权限了——", [760] = "等等，奥吉里……", [770] = "有什么问题吗？", [780] = "虽说我们确实是真心实意地想要帮助庇厄里亚……", [790] = "但决定将权限交给我们这件事，你不需要和奥吉塔商量吗？", [800] = "没必要吧。", [810] = "可是过去，你们一直都是一起做所有决定的呀。这么重要的事，总该和她说明一下吧……", [820] = "我觉得她会理解的。", [830] = "教授和大家肯定了我们的价值，奥吉塔想必也会感到高兴的。", [840] = "奥吉里……", [850] = "请大家收下权限吧。", [851] = "（接下权限密钥）", [852] = "还是和奥吉塔说明情况比较好。", [860] = "那我们这就开启沙盒模式？ ", [870] = "我还是觉得应该和奥吉塔说明情况——", [880] = "我说过了没有必要。", [890] = "奥吉塔并不能完全明白这对我们来说意味着什么。", [900] = "……什么声音？！", [910] = "你也听到了？好像是博物馆外传来的！", [920] = "……难道是奥吉塔吗？", [930] = "不，对方的人数很多，绝对不会是奥吉塔。", [940] = "萨可，高速侦查模式！", [950] = "吱唔！", [951] = "萨可", [960] = "吱吱——吱——！", [970] = "萨可说了什么？<size=30>真是的，下次应该配置一个翻译器……</size>", [980] = "净化者！有不少净化者正在赶来！", [990] = "是刚才漏掉了哪只，又去搬救兵了吗？", [1000] = "教授，收下权限密钥开启沙盒模式吧！", [1010] = "来不及，开启沙盒模式需要时间，敌人已经找上门了。", [1020] = "好吧，那就先处理掉它们！", [1030] = "正好刚才我还没打够呢！冲啊啊啊——", [1040] = "苏、苏尔！！！", [-999] = ""}
